INSERT INTO `tbl_organisasi` (`id_organisasi`, `id_skpa`, `nama_organisasi`, `tugas`, `fungsi`) VALUES
(1, 3, 'Inspektur', '<p>Melakukan pengawasan terhadap pelaksanaan urusan pemerintahan di daerah provinsi, pelaksanaan pembinaan atas penyelenggaraan pemerintahan daerah kab/kota dan pelaksanaan urusan pemerintahan di kab/kota melaksanakan tugas umum pemerintahan dan pembangunan di bidang Pemerintahan Umum</p>', '<p>a.&nbsp;&nbsp;&nbsp; perumusan kebijakan teknis bidang pengawasan dan fasilitas pengawasan;</p><p>b.&nbsp;&nbsp;&nbsp; pelaksanaan pengawasan internal terhadap kinerja dan keuangan melalui audit, reviu, evaluasi, pemantauan, dan kegiatan pengawasan lainnya;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan pengawasan untuk tujuan tertentu atas penugasan dari gubernur;</p><p>d.&nbsp;&nbsp;&nbsp; penyusunan laporan hasil pengawasan;</p><p>e.&nbsp;&nbsp;&nbsp; pelaksanaan administrasi inspektorat provinsi; dan</p><p>pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Gubernur sesuai dengan tugas dan fungsinya.</p>'),
(2, 3, 'Sekretariat', '<p>Melakukan pengelolaan urusan administrasi, umum, perlengkapan, peralatan, kerumahtanggaan, perpustakaan, keuangan, kepegawaian, ketatalaksanaan, hubungan masyarakat, hukum, perundang-undangan, pelayanan administrasi, penyusunan program dan pelaporan</p>', '<p>a.&nbsp;&nbsp; pelaksanaan urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan, pemeliharaan dan perpustakaan;</p><p>b.&nbsp;&nbsp; pembinaan kepegawaian, organisasi, ketatalaksanaan, hukum dan perundang-undangan serta pelaksanaan hubungan masyarakat</p><p>c.&nbsp;&nbsp;&nbsp; pengelolaan administrasi keuangan;</p><p>d.&nbsp;&nbsp; penyusunan program kerja tahunan, jangka menengah dan jangka panjang;</p><p>e.&nbsp;&nbsp;&nbsp; penyusunan rencana anggaran yang bersumber dari APBA, APBN dan sumber lainnya;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; penyusunan rencana strategis, laporan akuntabilitas kinerja dan rencana kinerja Inspektorat Aceh; dan</p><p>g.&nbsp;&nbsp;&nbsp; pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Inspektur Aceh sesuai dengan tugas dan fungsinya.</p>'),
(3, 44, 'Direktur', '<p>Melaksanakan tugas umum pemerintahan dan pembangunan di bidang pelayanan kesehatan dengan melakukan upaya pengobatan, penyembuhan atau pemulihan, upaya peningkatan derajat kesehatan, pencegahan penyakit dan upaya rujukan melalui tindakan medik dan non medik sesuai standar pelayanan</p>', '<ol><li>Pelaksanaan urusan ketatausahaan dinas;</li><li>Penyusunan program kerja tahunan, jangka menengah dan jangka panjang;</li><li>Penyusunan kebijakan teknis di bidang pelayanan medis, pelayanan penunjang medis dan non medis, pelayanan asuhan keperawatan, pelayanan rujukan, pelaksanaan pendidikan dan pelatihan, pelaksanaan penelitian dan pengembangan serta pengelolaan administrasi dan keuangan;</li><li>Pelayanan medis</li><li>Pelayanan penunjang medis dan non medis;</li><li>Pelayanan asuhan keperawatan;</li><li>Pelayanan rujukan;</li><li>Pelaksanaan pendidikan dan pelatihan;</li><li>Pelaksanaan penelitian dan pengembangan;</li><li>Pelayanan dan pengelolaan administrasi umum dan keuangan;</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Gubernur sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(4, 1, 'Sekda Aceh', '<p>Membantu gubernur dalam penyusunan kebijakan dan pengoordinasian administratif terhadap pelaksanaan tugas Perangkat Aceh serta pelayanan administratif.</p>', '<p>a.&nbsp;&nbsp; penyusunan kebijakan pemerintahan Aceh;</p><p>b.&nbsp;&nbsp; pelaksanaan dan pengoordinasian tugas perangkat Aceh;</p><p>c.&nbsp;&nbsp;&nbsp; pemantauan&nbsp; dan&nbsp; evaluasi&nbsp; pelaksanaan&nbsp; kebijakan&nbsp; pemerintahan Aceh;</p><p>d.&nbsp;&nbsp; pelayanan administratif dan pembinaan administrasi dan Aparatur Sipil Negara pada perangkat Aceh;</p><p>pelaksanaan fungsi kedinasan lainnya yang diberikan oleh gubernur yang berkaitan dengan tugas dan fungsinya</p>'),
(5, 1, '1. Asisten Pemerintahan dan Keistimewaan Aceh', '<p>Mempunyai tugas membantu Sekda Aceh dalam perumusan kebijakan, mengoordinasikan biro tata pemerintahan, hukum, perundang- undangan, keistimewaan dan kesejahteraan rakyat serta SKPA sesuai dengan pembidangan tugas Asisten Pemerintahan dan Keistimewaan Aceh.</p>', '<p>a.&nbsp;&nbsp; pelaksanaan penyusunan kebijakan dan program bidang tata pemerintahan, hukum, perundang-undangan, keistimewaan dan kesejahteraan rakyat serta SKPA sesuai dengan pembidangan tugas Asisten Pemerintahan dan Keistimewaan Aceh.</p><p>b.&nbsp;&nbsp; pelaksanaan koordinasi penyelenggaraan tugas dan program SKPA sesuai dengan pembidangan tugas Asisten Pemerintahan dan Keistimewaan Aceh;</p><p>c.&nbsp;&nbsp;&nbsp; pelaksanaan evaluasi penyelenggaraan program SKPA sesuai dengan pembidangan tugas Asisten Pemerintahan dan Keistimewaan Aceh;</p><p>d.&nbsp;&nbsp; pelaksanaan koordinasi kegiatan penyelenggaraan Keistimewaan Aceh yang meliputi bidang agama dan peran ulama, pendidikan dan kebudayaan, pariwisata, kesejahteraan dan pembinaan sosial;</p><p>e.&nbsp;&nbsp;&nbsp; pelaksanaan pembinaan administrasi di bidang tata pemerintahan, hukum, perundang-undangan, keistimewaan dan kesejahteraan rakyat serta SKPA sesuai dengan pembidangan tugas Asisten Pemerintahan dan Keistimewaan Aceh; dan pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Sekda.</p>'),
(7, 44, '1. Wakil direktur Administrasi dan Umum', '<p>Melakukan pengelolaan urusan umum, ketatausahaan, ketatalaksanaan, hukum dan peraturan perundang-undangan, kerumahtanggaan, perlengkapan, kemotoran, urusan dalam dan keamanan, kepegawaian, keuangan, sistem akuntansi, penyusunan program, pemasaran, kerjasama, pelayanan informasi, komunikasi dan kehumasan, penyuluhan, evaluasi dan pelaporan.</p>', '<ol><li>Pelaksanaan urusan Umum, ketatausahaan, rumah tangga, kemotoran serta urusan dalam dan keamanan;</li><li>Pelaksanaan urusan Hukum dan peraturan perundang-undangan;</li><li>Pelaksanaan urusan pelayanan administrasi pengadaan/ pemeliharaan barang inventaris, asset, perlengkapan dan peralatan kantor/rumah dinas/jabatan;</li><li>Pelaksanaan urusan kepegawaian;</li><li>Pelaksanaan urusan keuangan;</li><li>Pelaksanaan urusan administrasi penerimaan, pengeluaran dan mobilisasi dana;</li><li>Pelaksanaan sistem akuntansi pemerintahan;</li><li>Pelaksanaan akuntansi keuangan dan manajemen;</li><li>Pelaksanaan verifikasi dan pelaporan keuangan;</li><li>Pelaksanaan penyusunan program dan pemasaran;</li><li>Pelaksanaan urusan penyusunan rencana dan anggaran;</li><li>Pelaksanaan urusan pelayanan informasi, komunikasi, kerjasama dan penyuluhan;</li><li>Pelaksanaan evaluasi dan penyusunan laporan;</li><li>Pelaksanaan koordinasi dengan instansi dan atau lembaga terkait lainnya di bidang administrasi dan umum; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Direktur sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(8, 47, 'Kepala Bidang', '<p>Memimpin&nbsp; Badan dan melaksanakan tugas umum pemerintahan bidang bina ideologi, wawasan kebangsaan dan karakter bangsa, politik dalam negeri, ketahanan ekonomi, sosial, budaya dan organisasi kemasyarakatan, penanganan konflik dan kewaspadaan nasional</p>', '<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan dan pengoordinasian urusan ketatausahaan Badan;</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan dan pembinaan penyusunan program kerja tahunan, jangka menengah dan jangka panjang;</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp; Penyusunan kebijakan teknis dan strategis di bidang kesatuan bangsa dan politik;</p><p>4.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pembinaan ideologi, wawasan kebangsaan dan karakter bangsa;</p><p>5.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pembinaan dan fasilitasi politik dalam negeri;</p><p>6.&nbsp;&nbsp;&nbsp;&nbsp; Penyelenggaraan, pembinaan dan fasilitasi ketahanan ekonomi, sosial, budaya dan organisasi kemasyarakatan;</p><p>7.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penanganan konflik dan kewaspadaan nasional;</p><p>8.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pemantauan, evaluasi dan pelaporan;</p><p>9.&nbsp;&nbsp;&nbsp;&nbsp; Pembinaan UPTB;</p><p>10.&nbsp; Pelaksanaan koordinasi dengan instansi dan atau lembaga terkait lainnya di bidang kesatuan bangsa dan politik;</p><p>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Gubernur sesuai dengan tugas dan fungsinya.&nbsp;</p>'),
(9, 47, 'Sekretariat', '<p>Melakukan pengelolaan urusan administrasi, umum, perlengkapan, peralatan, kerumahtanggaan, perpustakaan, keuangan, kepegawaian, ketatalaksanaan, hukum, perundang-undangan, pelayanan administrasi, penyusunan program, data, informasi, pemantauan, evaluasi dan pelaporan.</p>', '<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan, pemeliharaan dan perpustakaan;</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp; Pembinaan kepegawaian, organisasi, ketatalaksanaan, hukum dan perundang-undangan serta pelaksanaan hubungan masyarakat;</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp; Pengelolaan administrasi keuangan;</p><p>4.&nbsp;&nbsp;&nbsp;&nbsp; Penyusunan perencanaan dan program kerja tahunan, jangka menengah dan jangka panjang;</p><p>5.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyusunan rencana strategis, laporan akuntabilitas kinerja dan rencana kinerja;</p><p>6.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan monitoring, evaluasi dan pelaporan; dan</p><p>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.&nbsp;</p>'),
(10, 4, 'Kepala Badan', '<p>Melaksanakan perencanaan pembangunan bidang ekonomi, sumber daya alam, infrastruktur dan kewilayahan, keistimewaan Aceh, pemerintahan sumber daya manusia, program dan pendanaan pembangunan, penelitian dan pengembangan, pengendalian dan evaluasi pembangunan daerah.</p>', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan urusan ketatausahaan Badan;</p><p>b.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyusunan program kerja tahunan, jangka menengah dan jangka panjang;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; perumusan kebijakan teknis di bidang perencanaan dan pembangunan aceh;</p><p>d.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasia, perencanaan pembangunan bidang ekonomi sumber daya alam</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasia, perencanaan pembangunan bidang infrastruktur dan kewilayahan</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian, perencanaan pembangunan bidang keistimewaan Aceh, pemerintahan dan sumber daya manusia;</p><p>g.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian, perencanaan pembangunan bidang program dan pendanaan pembangunan;</p><p>h.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian penyusuan rencana anggaran yang bersumber dari APBA, APBN, bantuan, pinjaman dan atau hibah luar negeri;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan penelitian dan pengembangan pendataan, pengendalian, pemantauan, evaluasi dan pelaporan pelaksanaan pembangunan di Aceh yang bersumber dari APBA, APBN, bantuan, pinjaman dan atau hibah luar negeri;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan penyiapan bahan rapat koordinasi, evaluasi dan pengendalian perencanaan pembangunan di Aceh;</p><p>pelaksanaan koordinasi dengan instansi dan/atau lembaga terkait lainnya di bidang perencanaan pembangunan Aceh;</p>'),
(11, 4, 'Sekretariat', '<p>Melakukan pengelolaan urusan administrasi, umum, perlengkapan, peralatan, kerumahtanggaan, perpustakaan, keuangan, kepegawaian, ketatalaksanaan, hukum, perundang-undangan, pelayanan administrasi di lingkungan Badan Perencanaan Pembangunan Daerah Aceh</p>', '<p>a.&nbsp;&nbsp; Pembinaan dan pemberian dukungan administrasi yang meliputi ketatausahaan, kerumahtanggaan, kerja sama, hubungan masyarakat, arsip. Perpustakaan dan dokumentasi Badan Perencanaan Pembangunan Daerah</p><p>b.&nbsp;&nbsp; Pembinaan kepegawaian, organisasi, ketatalaksanaa, hukum dan perundang-undangan</p><p>c.&nbsp;&nbsp;&nbsp; Pengelolaan administrasi keuangan</p><p>d.&nbsp;&nbsp; Penyelengaraan pengelolaan barang milik/kekayaan negara dan layanan pengadaan barang/jasa di lingkup Badan Perencanaan Pembangunan Daerah</p><p>e.&nbsp;&nbsp;&nbsp; Pengkoordinasian pemantauan, evaluasi, pengendalian dan penilaian atas kinerja pengadaan barang/jasa milik negara; dan</p><p>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(12, 47, 'Bidang Bina Ideologi, Wawasan Kebangsaan dan Karakter Bangsa,', '<p>Melakukan pembinaan ideologi, wawasan kebangsaan dan karakter bangsa, penerapan penghayatan dan pengamalan ideologi pancasila, pembinaan wawasan kebangsaan dan ketahanan nasional, pembinaan kewasapadaan nasional, pembinaan kerukunan antar suku, dan intrasuku, pelestarian nilai-nilai budaya, pembauran kebangsaan, umat beragama, ras, dan golongan lainnya</p>', '<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan, pengoordinasian dan pembinaan ideologi dan wawasan kebangsaan;</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan, pengendalian dan pembinaan karakter bangsa;</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan dan koordinasi penerapan, penghayatan dan pengamalan ideologi Pancasila, pembinaan wawasan kebangsaan dan ketahanan nasional, pembinaan kewasapadaan nasional, pembinaan kerukunan antar suku, dan intrasuku, umat beragama, ras, dan golongan lainnya;</p><p>4.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pembinaan penerapan penghayatan dan pengamalan pancasila, karakter dan wawasan kebangsaan, bela negara, sejarah kebangsaan, dan kewarganegaraan, pembauran dan pelestarian Binneka Tunggal Ika;</p><p>5.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan fasilitasi pembauran nilai-nilai wawasan kebangsaan, ketahanan bangsa, bela negara dan hak asasi manusia;</p><p>6.&nbsp;&nbsp;&nbsp;&nbsp; Pemberian bimbingan teknis, supervisi dan penerapan penghayatan dan pengamalan pancasila, karakter dan wawasan kebangsaan, bela negara, sejarah kebangsaan, dan kewarganegaraan, pembauran dan pelestarian Binneka Tunggal Ika; dan</p><p>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan Kesatuan Bangsa, Politik dan Perlindungan Masyarakat sesuai dengan tugas dan fungsinya.&nbsp;</p>'),
(13, 47, 'Bidang Politik Dalam Negeri', '<p>Melakukan pengembangan budaya politik, pendidikan politik dan fasilitasi partai politik serta Pemilu.</p>', '<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Penyiapan perumusan kebijakan bidang implementasi kebijakan politik, falisitasi lembaga pemerintahan dan perwakilan, falisitasi kelembagaan partai politik, pendidikan, etika dan budaya politik serta fasilitasi peningkatan demokrasi;</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan kebijakan bidang koordinasi implementasi kebijakan politik, falisitasi lembaga pemerintahan dan perwakilan, falisitasi kelembagaan partai politik, pendidikan, etika dan budaya poltik serta fasilitasi peningkatan demokrasi;</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pembinaan umum bidang implementasi kebijakan politik falisitasi lembaga pemerintahan dan perwakilan, falisitasi kelembagaan partai politik, pendidikan, etika dan budaya politik serta fasilitasi peningkatan demokrasi;</p><p>4.&nbsp;&nbsp;&nbsp;&nbsp; Pemberian bimbingan teknis dan supervisi bidang implementasi kebijakan politik falisitasi lembaga pemerintahan dan perwakilan, falisitasi kelembagaan partai politik, pendidikan, etika dan budaya poltik serta fasilitasi peningkatan demokrasi;</p><p>5.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaa pemantauan, evaluasi, pelaporan bidang implementasi kebijakan politik falisitasi lembaga pemerintahan dan perwakilan, falisitasi kelembagaan partai politik, pendidikan, etika dan budaya poltik serta fasilitasi peningkatan demokrasi; dan</p><p>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.&nbsp;</p>'),
(14, 4, 'Bidang Perencanaan Pembangunan Ekonomi dan Sumberdaya Alam', '<figure class=\"table\"><table><tbody><tr><td>Melakukan pengembangan sumber daya alam pengembangan industry, perdagangan, pariwisata, pengelolaan keuangan daerah, imvestasi dan ketenagakerjaan.</td></tr></tbody></table></figure>', '<figure class=\"table\"><table><tbody><tr><td><p>a.&nbsp;&nbsp; Pengkoordinasian penyusunan dokumen perencanaan pembangunan daerah (RPJPD, RPJMD dan RKPD) di bidang Ekonomi dan Sumber daya alam;</p><p>b.&nbsp;&nbsp; Pengkoordinasian penyusunan Rencana Strategis Perangkat Daerah dan Rencana Kerja Perangkat Daerah di bidang Ekonomi dan Sumber Daya Alam;</p><p>c.&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan Musrenbang (RPJPD, RPJMD dan RKPD) di bidang Ekonomi dan Sumber daya alam;</p><p>d.&nbsp;&nbsp; Pengkordinasiaan pelaksanaan kesepakatan dengan DPRD, terkait RPJPD, RPJMD dan RKPD di bidang Ekonomi dan Sumber daya alam;</p><p>e.&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan kesepakatan dengan DPRD terkait APBD di bidang Ekonomi dan sumber daya alam;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian sinergisitas dan harmonisasi kegiatan perangkat daerah provinsi di bidang Ekonomi dan Sumber daya alam;</p><p>g.&nbsp;&nbsp;&nbsp; Pengkoordinasian sinergisitas dan harmonisasi kegiatan Kementrian/Lembaga di provinsi dan kabupaten/kota di bidang Ekonomi dan Sumber daya alam</p><p>h.&nbsp;&nbsp; Pengkoordinasian dukungan pelaksanaan kegiatan pusat untuk prioritasn nasional di bidang Ekonomi dan sumber daya alam;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan kesepakatan bersama kerjasama antar daerah di bidang Ekonomi dan sumber daya alam;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian pembinaan teknis perencanaan kepada perangkat daerah provinsi di bidang Ekonomi dan sumber daya alam;</p><p>k.&nbsp;&nbsp; Pengkoordinasian pembinaan teknis perencanaan kepada perangkat daerah (BAPPEDA) kabupaten/kota di bidang Ekonomi dan Sumber daya alam;</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan dan pengkoordinasian perencanaan bidang zakat, infaq dan sadaqah; dan</p><p>m. Pelaksanaa tugas-tugas oleh kedinasan lainnya yang diberikan Kepala Badan sesuai dengan tugas dan fungsinya.</p></td></tr></tbody></table></figure>'),
(15, 4, 'Bidang Perencanaan Pembangunan  Infrastruktur dan Kewilayahan', '<figure class=\"table\"><table><tbody><tr><td>Melakukan perencanaan pembangunan dan pengembangan infrastruktur, ilmu pengetahuan dan teknologi, informasi dan telematika, persandian, pemukiman, lingkungan hidup, pemetaan wilayah dan pertanahan.</td></tr></tbody></table></figure>', '<p>a.&nbsp;&nbsp; Pengkoordinasian penyusunan dokumen perencanaan pembangunan daerah (RPJPD, RPJMD dan RKPD) di bidang infrastruktur dan kewilayahan;</p><p>b.&nbsp;&nbsp; Pengkoordinasian penyusunan Rencana Strategis Perangkat Daerah dan Rencana Kerja Perangkat Daerah di bidang infrastruktur dan kewilayahan;</p><p>c.&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan Musrenbang (RPJPD, RPJMD dan RKPD) di bidang infrastruktur dan kewilayahan;</p><p>d.&nbsp;&nbsp; Pengkordinasiaan pelaksanaan kesepakatan dengan DPRD, terkait RPJPD, RPJMD dan RKPD di bidang infrastruktur dan kewilayahan;</p><p>e.&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan kesepakatan dengan DPRD terkait APBD di bidang infrastruktur dan kewilayahan&nbsp; ;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian sinergisitas dan harmonisasi kegiatan perangkat daerah provinsi di bidang infrastruktur dan kewilayahan;</p><p>g.&nbsp;&nbsp;&nbsp; Pengkoordinasian sinergisitas dan harmonisasi kegiatan Kementrian/Lembaga di provinsi dan kabupaten/kota di bidang infrastruktur dan kewilayahan;</p><p>h.&nbsp;&nbsp; Pengkoordinasian dukungan pelaksanaan kegiatan pusat untuk prioritasn nasional di bidang infrastruktur dan kewilayahan;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan kesepakatan bersama kerjasama antar daerah di bidang infrastruktur dan kewilayahan;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian pembinaan teknis perencanaan kepada perangkat daerah provinsi di bidang infrastruktur dan kewilayahan;</p><p>k.&nbsp;&nbsp; Pengkoordinasian pembinaan teknis perencanaan kepada perangkat daerah (BAPPEDA) kabupaten/kota di bidang infrastruktur dan kewilayahan;</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan tugas-tugas oleh kedinasan lainnya yang diberikan Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(16, 47, 'Bidang Ketahanan Ekonomi, Sosial, Budaya dan Organisasi Kemasyarakatan', '<p>Pelaksana teknis bidang ketahanan ekonomi, sosial budaya dan organisasi kemasyarakatan</p>', '<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan kebijakan bidang koordinasi pelaksanaan pembinaan umum bidang ketahanan ekonomi, fasilitasi ketahanan pangan dan kesenjangan ekonomi, ketahanan sosial kemasyarakatan, ketahanan seni dan budaya, fasilitasi kerukunan umat beragama dan penghayat kepercayaan;</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pemberian bimbingan teknis dan supervisi bidang ketahanan ekonomi, fasilitasi ketahanan pangan dan kesenjangan ekonomi, ketahanan sosial kemasyarakatan, ketahanan seni dan budaya, fasilitasi kerukunan umat beragama dan penghayat kepercayaan;</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pemantauan, evaluasi dan pelaporan bidang ketahanan ekonomi, fasilitasi ketahanan pangan dan kesenjangan ekonomi, ketahanan sosial kemasyarakatan, ketahanan seni dan budaya, fasilitasi kerukunan umat beragama dan penghayat kepercayaan; dan</p><p>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan Kesatuan Bangsa dan Politik Aceh sesuai dengan tugas dan fungsinya.&nbsp;</p>'),
(17, 4, 'Bidang Perencanaan Pembangunan Keistimewaan Aceh, Pemerintahan  dan Sumber Daya Manusia', '<p>Melakukan perencanaan pembangunan bidang pengembangan sumber daya manusia, keistimewaan Aceh, pemerintahan dan kelembagaan, kependudukan serta kesejahteraan sosial</p>', '<p>a.&nbsp;&nbsp; Pengkoordinasian penyusunan dokumen perencanaan pembangunan daerah (RPJPD, RPJMD dan RKPD) di bidang Pengkoordinasian penyusunan dokumen perencanaan pembangunan daerah (RPJPD, RPJMD dan RKPD) di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>b.&nbsp;&nbsp; Pengkoordinasian penyusunan Rencana Strategis Perangkat Daerah dan Rencana Kerja Perangkat Daerah di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>c.&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan Musrenbang (RPJPD, RPJMD dan RKPD) di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>d.&nbsp;&nbsp; Pengkordinasiaan pelaksanaan kesepakatan dengan DPRD, terkait RPJPD, RPJMD dan RKPD di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>e.&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan kesepakatan dengan DPRD terkait APBD di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian sinergisitas dan harmonisasi kegiatan perangkat daerah provinsi di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>g.&nbsp;&nbsp;&nbsp; Pengkoordinasian sinergisitas dan harmonisasi kegiatan Kementrian/Lembaga di provinsi dan kabupaten/kota di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>h.&nbsp;&nbsp; Pengkoordinasian dukungan pelaksanaan kegiatan pusat untuk prioritas nasional di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan kesepakatan bersama kerjasama antar daerah di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian pembinaan teknis perencanaan kepada perangkat daerah provinsi di bidang Keistimewaan Aceh, Pemerintahan dan Sumber Daya Manusia;</p><p>k.&nbsp;&nbsp; Pengkoordinasian pembinaan teknis perencanaan kepada perangkat daerah (BAPPEDA) kabup</p>'),
(18, 38, 'Kepala Sekretariat', '<p>Melakukan pengelolaan urusan administrasi umum, program dan keuangan, hukum dan komunikasi publik, kajian strategis, risalah, persidangan, mendukung pelaksanaan tugas dan fungsi MPU, mengoordinasikan tenaga ahli MPU dan Badan otonom MPU.</p>', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp; Pengelolaan&nbsp; urusan ketatausahaan, kepegawaian, rumah tangga dan perlengkapan;</p><p>b.&nbsp;&nbsp;&nbsp; Pelaksanaan penyusunan program Sekretariat MPU dan memfasilitasi program MPU;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; pengelolaan keuangan;</p><p>d.&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; dan pengoordinasian hukum, fatwa dan peraturan perundang-undangan;</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; dan pengoordinasian fasilitasi sertifikasi dan sistem jaminan produk halal;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; dan pengoordinasian komunikasi publik;</p><p>g.&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; dan pengoordinasian pengkajian strategis, risalah dan persidangan;</p><p>h.&nbsp;&nbsp;&nbsp; Pelaksanaan fasilitasi, koordinasi dan pemberian pelayanan teknis MPU;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; monitoring, evaluasi dan pelaporan;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; koordinasi dengan instansi dan atau lembaga terkait lainnya dalam rangka mendukung tugas pokok dan fungsi MPU dan Sekretariat MPU;</p><p>pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Gubernur dan/atau Pimpinan MPU.&nbsp;</p>'),
(19, 38, 'Bagian Umum', '<p>&nbsp;</p>', '<p>&nbsp;</p>'),
(20, 44, '1.1. Bagian Tata Usaha', '<p>Melakukan urusan umum, ketatausahaan, ketatalaksanaan, hukum dan peraturan perundang-undangan, kepegawaian, kerumahtanggaan, administrasi perjalanan dan protokol administrasi pengadaan dan pemeliharaan perlengkapan kantor/rumah dinas/rumah jabatan dan kemotoran serta pendataan asset.</p>', '<ol><li>Pelaksanaan urusan umum dan pelayananan ketatausahaan;</li><li>Pelaksanaan administrasi perjalanan dinas dan protokoler;</li><li>Pelaksanaan urusan kemotoran;</li><li>Pelaksanaan urusan rumah tangga, pelayanan kebutuhan barang, inventaris, aset, perlengkapan dan peralatan kantor/rumah jabatan/rumah dinas;</li><li>Pelaksanaan Urusan Dalam dan Keamanan;</li><li>Pelaksanaan administrasi kepegawaian, pembinaan/ pengembangan organisasi, ketatalaksanaan, penegakan hukum dan perundang-undangan; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Administrasi dan Umum sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(21, 4, 'Bidang Program dan Pendanaan Pembangunan;', '<p>Melakukan perencanaan pembangunan bidang pendanaan APBA, Non APBA, kerjasama pembangunan dan penyusunan program serta pelaporan</p>', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyusunan rencana dana program kerja lingkup Badan Perencanaan Pembangunan Daerah;</p><p>b.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan dan pengkoordinasian penyiapan bahan penyusunan kebijakan bidang perencanaan dan pendanaan pembangunan;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan dan pengkoordinasian kegiatan pengelolaan administrasi perencanaan dan pendanaan pembangunan;</p><p>d.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan dan pengkoordinasian kerjasama pembangunan antar lembaga, wilayah, regional dan multilateral;</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyiapan bahan perumusan kebijakan di bidang pendanaan pembangunan&nbsp; ;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyiapan bahan perencanaan pembangunan di bidang pendanaan pembangunan;</p><p>g.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyiapan bahan peraturan peraturan perundang-undangan yang berhubungan dengan tugas pokok dan fungsi;</p><p>h.&nbsp;&nbsp;&nbsp;&nbsp; Pengintegrasian dan harmonisasi program-program pembangunan di daerah;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pengkoordinasian dan sinkronisasi pelaksanaan kebijakan perencanaan dan penganggaran di daerah; dan</p><p>j. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Pelaksanaan tugas-tugas oleh kedinasan lainnya yang diberikan Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(22, 44, '1.2. Bagian Akutansi', '<p>Melakukan pelaksanaan sistem akuntansi pemerintah, perbendaharaan, akuntansi manajemen, verifikasi dan pelaporan keuangan.</p>', '<ol><li>Pelaksanaan sistem akuntansi pemerintah;</li><li>Pelaksanaan sistem akuntansi manajemen;</li><li>Pelaksanaan perbendaharaan, verifikasi dan pelaporan keuangan;</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Administrasi dan Umum sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(23, 44, '1.3. Bagian Bina Program dan Pemasaran', '<p>Melakukan penyusunan rencana kerja, koordinasi penganggaran, pelayanan informasi dan komunikasi, pelaksanaan kerja sama dan pemasaran, pemantauan, evaluasi dan pelaporan.</p>', '<ol><li>Pelaksanaan penyusunan rencana kerja dan koordinasi penganggaran;</li><li>Pelaksanaan pelayanan informasi dan komunikasi dan kehumasan;</li><li>Pelaksanaan pemasaran produk unggulan kepada publik dan kerja sama dengan pihak ketiga;</li><li>Pelaksanaan pemantuan, evaluasi dan pelaporan pelaksanaan program; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Administrasi dan Umum sesuai dengan tugas dan fungsinya.</li></ol>'),
(24, 44, '1.4. Bagian Keuangan', '<p>Melakukan pengelolaan administrasi penerimaan, pengeluaran dan mobilisasi dana</p>', '<ol><li>Pelaksanaan penyusunan anggaran;</li><li>Pelaksanaan administrasi penerimaan;</li><li>Pelaksanaan administrasi pengeluaran;</li><li>Pelaksanaan mobilisasi dana;</li><li>Pelaksanaan administrasi penerimaan dan pengeluaran dana bantuan/hibah dan sumber-sumber lainnya; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Administrasi dan Umum sesuai dengan tugas dan fungsinya.</li></ol>'),
(25, 4, 'Bidang Penelitian dan Pengembangan', '<p>Melakukan koordinasi dan pelaksanaan penelitian dan pengembangan.</p>', '<p>a.&nbsp;&nbsp; Pelaksanaan perumusan kebijakan dan pelaksanaan penelitian dan pengembangan</p><p>b.&nbsp;&nbsp; Pelaksanaan pemberian dukungan atas penyelenggaran Pemerintahan Daerah di bidang penelitian dan pengembangan;</p><p>c.&nbsp;&nbsp;&nbsp; Pembinaan dan pelaksanaan tugas di bidang penelitian dan pengembangan;</p><p>d.&nbsp;&nbsp; Pelaksanaan pengkajian program pembangunan di daerah;</p><p>e.&nbsp;&nbsp;&nbsp; Pengkoordinasian pelaksanaan penelitian dan pengembangan daerah;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan analisa dan pengkajian perencanaan pembangunan darah;</p><p>g.&nbsp;&nbsp;&nbsp; Pelaksanaan analisa dan pengkajian kewilayahan; dan</p><p>h. &nbsp; &nbsp;Pelaksanaan tugas-tugas oleh kedinasan lainnya yang diberikan Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(26, 44, '2. Wakil Direktur Pengembangan SDM', '<p>Melakukan peningkatan mutu pendidikan dan pelatihan, penelitian, pengembangan dan penerapan ilmu pengetahuan dan teknologi kedokteran, keperawatan dan teknologi kesehatan lainnya</p>', '<ol><li>Pelaksanaan penyusunan rencana peningkatan kualitas sumber daya manusia RSUDZA;</li><li>Pelaksanaan koordinasi penyelenggaraan pendidikan dan pelatihan di bidang kedokteran, keperawatan dan pendidikan teknis kesehatan lainnya;</li><li>Pelaksanaan penyelarasan rencana dan diklat dengan kebutuhan peningkatan kualitas sumber daya manusia rumah sakit;</li><li>Pelaksanaan koordinasi penelitian, pengembangan dan penerapan ilmu pengetahuan dan teknologi kedokteran, keperawatan dan teknis kesehatan lainnya;</li><li>Pelaksanaan pelayanan perpustakaan manajemen kesehatan, ilmu pengetahuan/teknologi kedokteran, keperawatan dan teknis kesehatan lainnya; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Direktur sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(27, 44, '2.1. Bidang Pendidikan dan Latihan', '<p>Melakukan perencanaan dan koordinasi penyelenggaraan pendidikan dan pelatihan.&nbsp;</p>', '<ol><li>Pelaksanaan penyusunan materi pendidikan kedokteran, keperawatan dan pendidikan teknis kesehatan lainnya;</li><li>Pelaksanaan perencanaan dan pendidikan dan pelatihan kedokteran, keperawatan dan pendidikan teknis kesehatan;</li><li>Pelaksanaan penilaian keselarasan penugasan&nbsp; mengikuti diklat dengan kebutuhan pengembangan sumber daya manusia Rumah Sakit;</li><li>Pelaksanaan koordinasi penyelenggaraan praktek pendidikan kedokteran, keperawatan dan pendidikan teknis kesehatan; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Pengembangan Sumber Daya Manusia sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(28, 4, 'Bidang Pengendalian dan Evaluasi Pembangunan Daerah', '<p>Melakukan melakukan perencanaan pembangunan bidang pengendalian dan evaluasi pembangunan, data dan publikasi</p>', '<p>a.&nbsp;&nbsp; Pelaksanaan dan pengkoordinasian penyediaan data dan informasi untuk penyusunan rencana pembangunan Aceh</p><p>b.&nbsp;&nbsp; Pelaksanaan dan pengkoordinasian pengendalian rencana pembangunan Aceh;</p><p>c.&nbsp;&nbsp;&nbsp; Pelaksanaan dan pengkoordinasiaan evaluasi hasil pelaksanaan rencana pembangunan Aceh;</p><p>d.&nbsp;&nbsp; Pelaksanaan pengumpulan, penganalisa data dan informasi pembangunan untuk perencanaan pembangunan daerah;</p><p>e.&nbsp;&nbsp;&nbsp; Perumusan kebijakan pengendalian, evaluasi, data dan informasi pembangunan daerah;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan evaluasi terhadap kebijakan perencanaan pembangunan daerah, dan pelaksanaan rencana pembangunan daerah, serta hasil rencana pembangunan daerah;</p><p>g.&nbsp;&nbsp;&nbsp; Pelaksanaan pengendalian melalui pemantauan, supervisi dan tindak lanjut&nbsp; penyimpangan terhadap pencapaian tujuan agar program dan kegiatan sesuai dengan kebijakan pembangunan daerah;</p><p>h.&nbsp;&nbsp; Pelaksanaan identifikasi permasalahan pembangunan daerah berdasarkan data untuk mengetahui pembangunan berkelanjutan;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyajian dan pengamanan data informasi pembangunan daerah;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengamanan data melalui bahan cetak dan elektronik sebagai bahan dokumentasi</p><p>k.&nbsp;&nbsp; Penyusunan dan pengelolaan evaluasi dan pelaporan program dan kegiatan pembangunan daera; dan</p><p>l. &nbsp; &nbsp; Pelaksanaan tugas-tugas oleh kedinasan lainnya yang diberikan Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(29, 44, '2.2. Bidang Penelitian dan Pengembangan', '<p>Melakukan koordinasi penelitian, pengembangan dan penerapan ilmu pengetahuan dan teknologi kedokteran, keperawatan dan teknologi kesehatan lainnya.&nbsp;</p>', '<ol><li>Pelaksanaan koordinasi penelitian di bidang kedokteran, keperawatan dan teknologi kesehatan lainnya;</li><li>Pelaksanaan koordinasi pengembangan dan penerapan ilmu pengetahuan dan teknologi kedokteran, keperawatan dan teknologi kesehatan lainnya;</li><li>Pelaksanaan koordinasi dengan instansi dan atau lembaga terkait di bidang penelitian dan pengembangan medis dan non medis; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Pengembangan Sumber Daya Manusia sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(30, 44, '3. Wakil Direktur Pelayanan ', '<p>Melakukan pembinaan, koordinasi, pengawasan dan pengendalian terhadap pelayanan medis, spesialistis, upaya rujukan dan tindakan medis lainnya, pelayanan asuhan keperawatan, pelayanan pemeriksaan kesehatan, penerapan etika dan pengembangan profesi, serta koordinasi, pengawasan dan pengendalian terhadap pemanfaatan dan pengembangan fasilitas medis</p>', '<ol><li>Pelaksanaan pelayanan medis, spesialistis, upaya rujukan, pelayanan asuhan keperawatan, pelayanan Pemeriksaan Kesehatan dan tindakan medis lainnya;</li><li>Pelaksanaan pemantauan, pengawasan, penilaian dan pengendalian penggunaan fasilitas dan kegiatan pelayanan medis, spesialistis, rujukan dan tindakan medis lainnya;</li><li>Pelaksanaan koordinasi perencanaan kebutuhan alat-alat dan fasilitas pelayanan medis, spesialis, rujukan dan tindakan medis lainnya;</li><li>Pelaksanaan koordinasi&nbsp; pemanfaatan, pemeliharaan dan pengembangan fasilitas medis, spesialis, rujukan dan tindakan medis lainnya;</li><li>Pelaksanaan bimbingan program dan pelayanan asuhan keperawatan, penerapan etika profesi, pengawasan mutu dan standar pelayanan; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Direktur sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(31, 4, 'Kelompok Jabatan Fungsional', '<p>Melaksanakan sebagian tugas Pemerintah Aceh sesuai dengan keahlian dan kebutuhan</p>', '<p>&nbsp;</p>'),
(32, 44, '3.1. Bidang Pelayanan Medis', '<p>Melakukan pembinaan, koordinasi, pengawasan dan pengendalian terhadap kebutuhan dan penggunaan fasilitas pelayanan medis, kegiatan pelayanan medis, spesialistis, upaya rujukan dan tindakan medis lainnya.</p>', '<ol><li>Pembinaan kegiatan pelayanan medis dan spesialistis;</li><li>Pembinaan kegiatan upaya rujukan dan tindakan medis lainnya;</li><li>Pembinaan kegiatan pelayanan pemeriksaan kesehatan;</li><li>Pelaksanaan pemantauan, pengawasan, penilaian dan pengendalian penggunaan fasilitas dan kegiatan pelayanan medis, spesialistis, rujukan dan tindakan medis lainnya;</li><li>Pelaksanaan koordinasi perencanaan kebutuhan alat-alat dan fasilitas pelayanan medis, spesialis, rujukan dan tindakan medis lainnya;</li><li>Pelaksanaan koordinasi&nbsp; pemanfaatan, pemeliharaan dan pengembangan fasilitas medis, spesialis, rujukan dan tindakan medis lainnya; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Pelayanan sesuai dengan tugas dan fungsinya.</li></ol>'),
(33, 5, 'Kepala', '<p>Memimpin Badan Pengelolaan Keuangan Aceh dalam melaksanakan pengelolaan keuangan bidang pendapatan, anggaran, barang dan kekayaan milik Aceh, perbendaharaan, akutansi dan pembinaan serta evaluasi anggaran kabupaten/kota sesuai peraturan perundang-undangan.</p>', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp; pembinaan dan pengendalian urusan ketatausahaan dan kerumahtanggan</p><p>b.&nbsp;&nbsp;&nbsp; pembinaan dan pengendalian penyusunan program kerja tahunan, jangka menengah dan jangka panjang</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanan rumusan kebijakan teknis bidang pengelolaan keuangan</p><p>d.&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanaan pengelolaan keuangan bidang pendapatan</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanaan pengelolaan keuangan bidang anggaran</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanaan pengelolaan keuangan bidang barang dan kekeyaaan milik Aceh</p><p>g.&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanaan pengelolaan keuangan bidang perbendaharaan</p><p>h.&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanaan pengelolaan keuangan bidang akuntansi</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanaan pembinaan dan evaluasi anggaran kabupaten/kota</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian perumusan kebijakan teknis&nbsp; administrasi dan teknis pelaksanan penyusunan anggaran</p><p>k.&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian pelaksanaan asas umum pengelolaan keuangan Aceh</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pengkoordinasian penyiapan dan rekomenasi terhadap pejabat-pejabat yang mengelola keuangan daerah</p><p>m.&nbsp;&nbsp; pengkoordinasian, pengendalian penyusunan dan penyiapan s truktur APBA</p><p>n.&nbsp;&nbsp;&nbsp; pengkoordinasian pelaksanaan pemungutan pendapatan daerah yang telah ditetapkan dengan Qanun</p><p>o.&nbsp;&nbsp;&nbsp; pengkoordinasian dan pengendalian pelaksanaan pemungutan pajak daerah</p><p>p.&nbsp;&nbsp;&nbsp; pengkoordinasian penyusunan dan penyiapan penetapan anggaran pendapatan dan belanja Aceh (APBA)</p><p>q.&nbsp;&nbsp;&nbsp; pengkoordinasian penyuunsnan dan penyiapan perubahan anggaarn'),
(34, 5, 'Sekretariat', '<p>Melakukan pengelolaan urusan administrasi, umum, perlengkapan, peralatan, kerumahtanggaan, perpustakaan, keuangan, aset, kepegawaian, ketatalaksanaan, hukum, perundang-undangan, pelayanan administrasi di lingkungan Badan.</p>', '<p>a.&nbsp;&nbsp; Pengendalian teknis urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan, pemeliharaan dan perpustakaan</p><p>b.&nbsp;&nbsp; Pembinan teknsi urusan kepegawaian, organisasi, ketatalaksanaan, hukum dan perundang-undangan serta pelaksanaan hubungan masyarakat</p><p>c.&nbsp;&nbsp;&nbsp; Pengawasan kegiatan pengelolaan adminsitrasi keuangan sesuai kebutuhan dan ketentuan.</p><p>d.&nbsp;&nbsp; Pengendalian teknis penyusunan program kerja tahunan, jangka menengah dan jangka panjang</p><p>e.&nbsp;&nbsp;&nbsp; Pelaksanaan monitoring, evaluasi dan pelaporan</p><p>f. &nbsp; &nbsp;Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya</p>'),
(35, 44, '3.2. Bidang Keperawatan', '<p>Melakukan bimbingan&nbsp; pelaksanaan asuhan dan pelayanan keperawatan, penerapan etika, pengembangan profesi, pengendalian mutu dengan menerapkan prinsip profesionalisme keperawatan.&nbsp;</p>', '<ol><li>Pelaksanaan bimbingan program dan pelayanan asuhan keperawatan;</li><li>Pelaksanaan penerapan etika profesi dan pengendalian mutu keperawatan;</li><li>Pelaksanaan pengembangan sumber daya keperawatan;</li><li>Pelaksanaan pengawasan mutu dan standar keperawatan; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Pelayanan sesuai dengan tugas dan fungsinya</li></ol>'),
(36, 44, '4. Wakil Direktur Penunjang', '<p>Melakukan pembinaan, koordinasi, pengawasan dan pengendalian terhadap pelayanan penunjang medis untuk penegakan diagnosa dan menunjang pemulihan, pemanfaatan dan pengembangan sarana/ prasarana penunjang medis, perencanaan kebutuhan dan distribusi logistik fasilitas penunjang medis/non medis serta penyusunan rencana pemeliharaan fasilitas penunjang medis/non medis</p>', '<ol><li>Pelaksanaan pelayanan penunjang medis;</li><li>Pelaksanaan pengembangan sarana/prasarana pelayanan penunjang medis untuk penegakan diagnosa;</li><li>Pelaksanaan koordinasi perencanaan kebutuhan dan distribusi logistik dan fasilitas pelayanan penunjang medis/non medis;</li><li>Pelaksanaan koordinasi perencanaan pemeliharaan fasilitas pelayanan penunjang medis/non medis;</li><li>Pelaksanaan inventarisasi sarana, prasarana pelayanan penunjang medis dan non medis;</li><li>Pelaksanaan pengawasan dan pengendalian penggunaan fasilitas pelayanan penunjang medis dan non medis; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Direktur RSUDZA sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(37, 44, '4.1. Bidang Pengadaan Sarana Penunjang', '<p>Melakukan inventarisasi, analisa kebutuhan dan pengembangan sarana, prasarana pelayanan penunjang medis dan non medis serta melakukan pembinaan, koordinasi, pengawasan dan pengendalian terhadap kegiatan pelayanan penunjang medis/non medis.</p>', '<ol><li>Pelaksanaan inventarisasi sarana dan prasarana pelayanan penunjang medis dan non medis;</li><li>&nbsp;Pelaksanaan penyusunan analisa kebutuhan sarana dan prasarana pelayanan penunjang medis dan non medis;</li><li>Pelaksanaan koordinasi rencana pengadaan, pemeliharaan dan pengembangan sarana, prasarana pelayanan penunjang medis dan non medis;</li><li>Pelaksanaan pengawasan dan pengendalian penggunaan sarana, prasarana pelayanan penunjang medis dan non medis; dan e. pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Penunjang sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(38, 44, '4.2. Bidang Logistik dan Fasilitas', '<p>Melakukan penyusunan rencana distribusi logistik/fasilitas pelayanan menunjang medis/non medis serta penyiapan bahan koordinasi, pembinaan, pengawasan dan pengendalian terhadap penggunaan dan pemeliharaan fasilitas pelayanan penunjang&nbsp; medis dan non medis.</p>', '<ol><li>Pelaksanaan inventarisasi logistik penunjang medis dan non medis;</li><li>Pelaksanaan penyusunan rencana distribusi logistik/ fasilitas pelayanan medis dan non medis;</li><li>Pelaksanaan koordinasi pemeliharaan fasilitas pelayanan&nbsp; penunjang medis dan non medis;</li><li>Pelaksanaan pengawasan dan pengendalian penggunaan logistik pelayanan penunjang medis dan non medis;</li><li>Pelaksanaan pemantauan, pengawasan, penilaian dan pengendalian penggunaan logistik, fasilitas pelayanan penunjang medis/non medis; dan</li><li>Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Wakil Direktur Penunjang sesuai dengan tugas dan fungsinya.&nbsp;</li></ol>'),
(39, 5, 'Bidang Anggaran Aceh', '<p>Melakukan penyusunan Rencana Anggaran Pendapatan dan Belanja Aceh,&nbsp; Anggaran Pendapatan dan Belanja Aceh, Rencana Anggaran Pendapatan dan Belanja Aceh Perubahan, Anggaran Pendapatan dan Belanja Aceh Perubahan, pengendalian anggaran dan pembiayaan anggaran belanja</p>', '<p>a.&nbsp;&nbsp; Pelaksanaan koordinasi pengealokasian anggaran dalam penyusunan KUA PPAS dan KUPA/PPAS Perubahan.</p><p>b.&nbsp;&nbsp; Pelaksanaan koordinasi penyusunan RKA/DPA dan atau KDAP/DPPA SKPA.</p><p>c.&nbsp;&nbsp;&nbsp; Penyusunan Qanun tentang APBA dan Peraturan Kepala Daerah tentang penjabaran APBA</p><p>d.&nbsp;&nbsp; Penyusunan peraturan kepa daerah tentang teknis penyusunan anggaran SKPA</p><p>e.&nbsp;&nbsp;&nbsp; Pelaksanaan pengoordinasian perencanaan anggaran pendapatan</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengoordinasian perencanaan anggaran belanja daerah.</p><p>g.&nbsp;&nbsp;&nbsp; Pelaksanaan pengoordinasian perencanaan anggaran pembiayaan</p><p>h.&nbsp;&nbsp; Pelaksanan penyediaan anggaran kas</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan montitoring, evaluasi dan pelaporan</p><p>j. &nbsp; &nbsp; Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(40, 5, 'Bidang Pengelolaan Barang Milik Aceh', '<p>Melakukan menyelengarakan pengelolaan barang milik daerah meliputi perencanaan kebutuhan, inventarisasi, penatausahaan, pemindahtangan, penghapusan, legalitas, serta pengamanan dan pemeliharaan barang milik Aceh.&nbsp;</p>', '<p>a.&nbsp;&nbsp; pengkoordinasian, pengkajian dan perumusan rancangan kebijakan teknis dan atau pedoman teknis dibidang pengelolaan barang milik Aceh meliputi perencanaan kebutuhan dan penganggaran, pengadaan, penggunan, pemanfaatan, pengamanan dan pemeliharaan, penilaian, pemindahtanganan, pemusnahan, penghapusan, epenatausahan, pembinaan, sistem informasi, pengawasan dan pengendalian.</p><p>b.&nbsp;&nbsp; pengendalian dan pengkoordinasian penyusunan program kerja di Bidang pengelolaan barang milik Aceh meliputi perencanaan kebutuhan dan penganggaran, pengadaan, penggunaan, pemanfaatan, pengamanan dan pemeliharaan, penilaian, pemindahtanganan, pemusnahan, penghapusan, penatausahaan, pembinaan, sistem informasi, pengawasan dan pengendalian</p><p>c.&nbsp;&nbsp;&nbsp; pengendalian teknis penyelenggaraan pengelolaan barang milik Aceh meliputi perencanaan kebutuhan dan penganggaran, pengadaan, penggunaan, pemanfaatan, pengamanan dan pemeliharaan, penilaian, pemindahtanganan, pemusnahan, penghapusan, penatausahaan, pembinaan, sistem informasi, pengawasan dan pengendalian</p><p>d.&nbsp;&nbsp; pengendalian dan pengkoordinasian teknis pengumpulan data barang milik Aceh</p><p>e.&nbsp;&nbsp;&nbsp; pengendalian dan pengkoordinasian pelaksanaan teknis analisa data meliputi data perencanaan kebutuhan dan penganggaran, pengadaan, penggunaan, pemanfaatan, pengamanan dan pemeliharaan, penilaian, pemindahtanganan, pemusnahan, penghapusan, penatausahaan, pembinaan, sistem informasi, pengawasan dan pengendalian</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan pengaman fisik, administrasi dan hukum barang milik Aceh</p><p>g.&nbsp;&nbsp;&nbsp; pelaksanan dan pengkoordinasian inventarisasi barang milik Aceh</p><p>h.&nbsp;&nbsp; pelaksanan pengkajian dan penyiapan bahan pertimbangan persetujuan dalam penyusunan rencana kebutuhan, pemeliharaan dan peerawatan barang milik Aceh</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan inventarisasi barang milik Aceh</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; penyelen'),
