INSERT INTO `tbl_organisasi` (`id_organisasi`, `id_skpa`, `nama_organisasi`, `tugas`, `fungsi`) VALUES
(48, 5, 'Bidang Pendapatan', '<p>Melakukan pengumpulan pendapatan asli Aceh, dana perimbangan dan lain pendapatan yang sah serta pengembangan pendapatan asli Aceh.</p>', '<p>a.&nbsp;&nbsp; Penyusunan program kerja bidang pendapatan</p><p>b.&nbsp;&nbsp; Perumusan kebijakan teknis bidang pemungutan pajak Aceh, restribusi Aceh dan pungutan Aceh lainnya</p><p>c.&nbsp;&nbsp;&nbsp; Pelaksanaan pelayanan pendaftaran dan pemungutan pajak Aceh, restribusi Aceh dan pungutan lainnya</p><p>d.&nbsp;&nbsp; Penyiapan bahan dan melakukan pembinan pemungutan pajak Aceh</p><p>e.&nbsp;&nbsp;&nbsp; Pelaksanaan pendaftaran, penetapan, pendapatan dan penilaian pajak dan restribusi Aceh</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pengkajian pengembangan potensi penerimaan pajak Aceh unggulan , restribusi Aceh unggulan</p><p>g.&nbsp;&nbsp;&nbsp; Pengkajian pengembangan potensi penerimaan pendapatan asli Aceh di luar pajak Aceh dan restribusi Aceh</p><p>h.&nbsp;&nbsp; Pelaksanaan koordinasi penerimaan bagi hasil pajak dan bukan pajak</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan sosialisasi dan konsultasi kebijakan pajak daerah dan restribusi daerah</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan koordinasi pemungutan pajak Aceh dan restribusi Aceh</p><p>k.&nbsp;&nbsp; Pemantauan evaluasi dan pelaporan penerimaan pendapatan</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan evaluasi dan pelaporan pengembangan pendapatan asli Aceh</p><p>m. Pelaksanaan penagihan dan doleansi pajak Aceh</p><p>n.&nbsp;&nbsp; Pelaksanaan operasional lapangan</p><p>o.&nbsp;&nbsp; Pelaksanaan pemantauan, evaluasi dan pelaporan pengembangan pendapatan asli Aceh</p><p>p.&nbsp;&nbsp; Pelaksanaan monitoring, evaluasi dan pelaporan</p><p>q. &nbsp; Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya</p>'),
(49, 5, 'Bidang Perbendaharaan', '<p>Melakukan penataan administrasi keuangan, pengelolaan dan penatausahaan kas daerah, meneliti kebenaran penagihan, realisasi anggaran, registrasi penerimaan, realisasi pengeluaran dan pembinaan pembendaharan pemegang kas, pembinaansatuan pemegang kas dan menerbitan Surat Perintah Pencairan Dana.</p>', '<p>a.&nbsp;&nbsp; Pelaksanaan pembinaan dan pengendalian teknis perumusan rancangan kebijakan teknis di bidang perbendaharaan;</p><p>b.&nbsp;&nbsp; Pelaksanaan pembinaan dan pengendalian teknis penyusunan program kerja di bidang perbendaharaan;</p><p>c.&nbsp;&nbsp;&nbsp; Pelaksanaan pembinaan teknis terhadap pelaksanaan kegiatan di bidang perbendaharaan</p><p>d.&nbsp;&nbsp; Pelaksanaan pembinaan satuan pemegang kas atau bendahara;</p><p>e.&nbsp;&nbsp;&nbsp; Pelaksanaan pembinaan dan pengendalian teknis terhadap pelaksanaan pengujian kebenaran penagihan;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengendalian teknis pelaksanaan pemeriksaan, penelitian terhadap realisasi anggaran;</p><p>g.&nbsp;&nbsp;&nbsp; Pelaksanaan pengkoordinasian pengelolaan dana perimbangan dan dana transfer lainnya;</p><p>h.&nbsp;&nbsp; Pelaksanaan pengkoordinasian penempatan uang daerah dengan membuka rekening kas umum daerah;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengkoordinasian penyusunan laporan realisasi penerimaan dan pengeluaran kas daerah, laporan aliran kas, dan pelaksanaan pemungutan/pemotongan dan penyetoran perhitungan Pihak Ketiga berdasarkan Surat Perintah Pencairan Dana;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengkoordinasian penerbitan Surat Penyediaan Dana Belanja Langsung dan Tidak Langsung;</p><p>k.&nbsp;&nbsp; Pelaksanaan pengkoordinasian pengelolaan kas daerah;</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengkoordinasian pemindahbukuan uang kas daerah;</p><p>m. Pelaksanaan pengkoordinasian penatausahaan pembiayaan daerah;</p><p>n.&nbsp;&nbsp; Pelaksanaan pengkoordinasian pemrosesan, penerbitan dan pendistribusian lembar Surat Perintah Pencairan Dana;</p><p>o.&nbsp;&nbsp; Pelaksanaan pengkoordinasian&nbsp; pelaksanaan dan penelitian kelengkapan dokumen Surat Permintaan Pembayaran dan Surat Perintah Membayar, pemberian pembebanan rincian penggunaan atas pengesahan SPJ gaji dan non gaji serta penerbitan SKPP;</p><p>p.&nbsp;&nbsp; Pelaksanaan pengkoordinasian '),
(50, 5, 'Bidang Akuntansi', '<p>Menyelenggarakan perumusan kebijakan teknis dan menyelenggarakan penataan system akutansi atas transaksi keuangan, asset, utang piutang, dan ekuitas termasuk transaksi pembiayaan dan perhitungan dalam rangka penyusunan laporan keuangan yang terdiri dari Laporan Realisasi Anggaran (LRA), Laporan Perubahan Saldo Anggaran Lebih (LPSAL), Laporan Operasional (LO), Laporan Perubahan Equitas (LPE), Neraca dan Laporan Arus Kas (LAK) dan Catatan Atas Laporan Keuangan (CaLK) sebagai dokumen pertanggung jawaban pelaksanaan Anggaran Pendapatan Belanja Aceh sesuai dengan standar akutansi pemerintahan.</p>', '<p>a.&nbsp;&nbsp; Pelaksanaan pembinaan dan pengendalian teknis terhadap penyusunan program kerja di bidang akuntansi;</p><p>b.&nbsp;&nbsp; Penyelenggaraan akuntansi dan sistem informasi pengelolaan keuangan daerah;</p><p>c.&nbsp;&nbsp;&nbsp; Penyusunan serta perumusan kebijakan dan pedoman teknis penyelenggaraan akuntansi keuangan daerah;</p><p>d.&nbsp;&nbsp; Pelaksanaan pembinaan dan pengendalian teknis perumusan kebijakan dan pedoman teknis akuntansi keuangan daerah;</p><p>e.&nbsp;&nbsp;&nbsp; Pelaksanaan pengendalian dan pengkoordinasian teknis terhadap pelaksanaan sistem akuntansi pendapatan, beban dan belanja, pembiayaan, aset, kewajiban serat ekuitas dalam penyusunan laporan keuangan daerah sebagai laporan pertanggungjawaban pelaksanaan Anggaran Pendapatan Belanja Aceh;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengendalian dan pengkoordinasian teknis terhadap pelaksanaan rekonsiliasi antara rekening Kas Umum Aceh pada bank dengan catatan pembukuan;</p><p>g.&nbsp;&nbsp;&nbsp; Pelaksanaan pembinaan pengendalian dan pengkoordinasian teknis terhadap laporan keuangan satuan kerja perangkat Aceh;</p><p>h.&nbsp;&nbsp; Pelaksanaan proses konsolidasi sistem informasi keuangan dan laporan pertanggungjawaban satuan kerja perangkat Aceh dalam penyesuaian laporan keuangan pemerintah daerah;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan tindak lanjut hasil evaluasi laporan keuangan daerah;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan monitoring, evaluasi dan pelaporan kegiatan bidang; dan</p><p>k. &nbsp; &nbsp;Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(51, 5, 'Bidang Pembinaan dan Evaluasi Anggaran Kabupaten/Kota', '<p>Melakukan pembinaan dalam rangka pengelolaan anggaran pendapatan dan belanja daerah kabupaten/kota</p>', '<p>a.&nbsp;&nbsp; pelaksanaan pembinaan dan pengendalian teknis terhadap penyusunan program kerja di bidang akuntasi</p><p>b.&nbsp;&nbsp; penyelenggaraan akuntasi dan sistem ifnormasi pengelolaan keuangan daerah</p><p>c.&nbsp;&nbsp;&nbsp; penyusunan serta perumusan kebijakan dan pedoman teknsi penyelenggaraan akuntansi keuangan daerah</p><p>d.&nbsp;&nbsp; pelaksanaan pembinaan dan pengendalian teknis perumusan kebijakan dan pedoman teknis akutansi keuangan daerah</p><p>e.&nbsp;&nbsp;&nbsp; pelaksanan pengendalian dan pengkoordinasian teknis terhadap pelaksanaan sistem akuntansi pendapatan, beban dan belanja, pembiayaan, aset, kewajiban serta ekuitas dalam penyusunan laporan keuangan daerah sebagai laporan pertanggungjawaban pelaksanaan Anggaran Pendapatan Belanja Aceh</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan pengendalian dan pengkordinasian teknis terhadap pelaksanaan rekonsiliasi antara rekening Kas Umum Aceh pada bank dengan catatan pembukuan</p><p>g.&nbsp;&nbsp;&nbsp; pelaksanan pembinaan, pengendalian, dan pengkoordinasian teknis terhdap laporan keuangan satuan kerja perangkat Aceh</p><p>h.&nbsp;&nbsp; pelaksanaan proses konsolidasi sistem informasi keuangan dan laporan&nbsp; pertanggungjawaban satuan kerja perangkat Aceh dalam penyesuaian laporan keuangan pemerintah daerah</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan tindak lanjut hasil evaluasi pelaporan keuangan daerah</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan monitoring evalusi dan pelaporan kegiatan bidang</p><p>k. &nbsp; &nbsp;pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badang sesuai dengan tugas dan fungsinya</p>'),
(52, 5, 'Kelompok Jabatan Fungsional.', '<p>Melaksanakan sebagian tugas Badan Pengelolaan Keuangan Aceh sesuai dengan keahlian dan kebutuhan.&nbsp;</p>', '<p>&nbsp;</p>'),
(53, 6, 'Kepala Badan', '<p>Melaksanakan tugas umum pemerintahan dan pembangunan di bidang pembinaan dan pengembangan aparatur sipil negara, mutasi kepegawaian, manajemen kinerja, kesejahteraan dan informasi kepegawaian sesuai dengan peraturan perundang-undangan</p>', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan urusan ketatausahaan Badan;</p><p>b.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyusunan program kerja tahunan, jangka menengah dan jangka panjang;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; perumusan kebijakan teknis kepegawaian serta evaluasi;</p><p>d.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pelayanan administrasi Kepegawaian;</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyiapan rancangan qanun di bidang kepegawaian sesuai dengan norma standar dan prosedur yang ditetapkan pemerintah;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyiapan formasi dan pengadaan pegawai;</p><p>g.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyiapan mutasi jabatan struktural instansi pemerintah Aceh dan penyelesaian konsultasi jabatan struktural dilingkungan pemerintah Aceh dan pemerintah Kabupaten/Kota dalam Provinsi Aceh;</p><p>h.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pembinaan dan pengembangan jabatan fungsional;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyiapan dan pelayanan administrasi serta pelaksanaan pengangkatan, pemindahan dan pemberhentian pegawai negeri sipil daerah sesuai dengan norma, standar dan prosedur yang ditetapkan dalam peraturan perundang-undangan;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyelenggaraan rekuitmen dan seleksi pendidikan tenaga kader;</p><p>k.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyelenggaraan administrasi kepangkatan pegawai;</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penyiapan dan penetapan penggajian dan pensiun pegawai;</p><p>m.&nbsp;&nbsp;&nbsp;&nbsp; penyelenggaraan administrasi mutasi wilayah kerja pegawai Aparatur Sipil Negara di lingkungan Pemerintah Aceh dan Pemerintah kabupaten/kota dalam Provinsi Aceh;</p><p>n.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pembinaan, pengawasan dan pengendalian terhadap pengelolaan manajemen kepegawaian dilingkungan pemerintah kabupaten/kota;</p><p>o.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; penetapan tunjangan dan kesejahteraan serta peruma'),
(54, 6, 'Sekretariat', '<p>Melakukan pengelolaan urusan administrasi, umum, kelengkapan, peralatan, kerumahtanggaan, perpustakaan, keuangan, kepegawaian, ketatalaksanaan, hukum, perundang-undangan, pelayanan administrasi di lingkungan Badan Kepegawaian Aceh</p>', '<p>a.&nbsp;&nbsp; penyusunan rencana strategis, rencana kinerja dan anggaran, penyusunan program, penelitian, pengkajian, data, informasi, pemantauan, evaluasi dan pelaporan kegiatan;</p><p>b.&nbsp;&nbsp; pengelolaan administrasi keuangan;</p><p>c.&nbsp;&nbsp;&nbsp; pembinaan kepegawaian, organisasi, ketatalaksanaan, hukum dan perundang-undangan serta pelaksanaan hubungan masyarakat, pelaksanaan urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan, pemeliharaan dan perpustakaan; dan</p><p>d. &nbsp; &nbsp;pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya</p>'),
(55, 6, 'Bidang Pembinaan dan Pengembangan Pegawai', '<p>Mempunyai tugas melakukan penyusunan formasi, pengadaan pegawai, seleksi tugas belajar dan pendidikan kader, pembinaan jabatan pimpinan, administrasi, rekomendasi mutasi pejabat struktural eselon II kabupaten/kota, pembinaan dan pengembangan jabatan fungsional umum dan khusus dan pindah wilayah kerja.</p>', '<p>a.&nbsp;&nbsp; pelaksanaan penyusunan formasi dan pengadaan pegawai;</p><p>b.&nbsp;&nbsp; pelaksanaan penyiapan rumusan bahan kebijakan pengembangan sistem pembinaan karier, inventarisasi keterampilan, keahlian dan bakat kepegawaian;</p><p>c.&nbsp;&nbsp;&nbsp; pelaksanaan pengembangan pendidikan kader (analisis, seleksi, pendidikan, evaluasi);</p><p>d.&nbsp;&nbsp; pelaksanaan ujian dinas untuk peryaratan kenaikan pangkat;</p><p>e.&nbsp;&nbsp;&nbsp; pelaksanaan penyusunan kompetensi jabatan pimpinan dan administrasi;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan pembinaan jabatan pimpinan dan administrasi di lingkungan pemerintah aceh;</p><p>g.&nbsp;&nbsp;&nbsp; pelaksanaan penyusunan rekomendasi mutasi&nbsp; pejabat struktural eselon II kabupaten/kota;</p><p>h.&nbsp;&nbsp; pelaksanaan pembinaan dan pengembangan jabatan fungsional umum dan khusus;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan penilaian angka kredit jabatan fungsional khusus;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan penyelesaian administrasi pindah wilayah kerja dilingkungan pemerintah aceh, antar wilayah dan antar kabupaten/kota;</p><p>k.&nbsp;&nbsp; pelaksanaan koordinasi dengan instansi dan atau lembaga terkait lainnya di bidang pembinaan dan pengembangan pegawai Aparatur Sipil Negara; dan</p><p>l. &nbsp; &nbsp;pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(56, 6, 'Bidang Mutasi Kepegawaian', '<p>Melakukan penyiapan bahan pertimbangan kenaikan pangkat, sistem penggajian, status kepegawaian dan penetapan pensiun</p>', '<p>a.&nbsp;&nbsp; pelaksanaan persetujuan dan penetapan keputusan kenaikan pangkat, peninjauan masa kerja;</p><p>b.&nbsp;&nbsp; pelaksanaan penyiapan bahan pertimbangan teknis kenaikan pangkat pegawai negeri sipil bagi pegawai kab./kota untuk golongan ruang pembina IV/a keatas;</p><p>c.&nbsp;&nbsp;&nbsp; pelaksanaan pemberian nomor surat persetujuan dan pertimbangan kepangkatan dan penggajian;</p><p>d.&nbsp;&nbsp; pelaksanaan penyusunan bahan pertimbangan teknis kenaikan gaji dan tunjangan pegawai negeri sipil;</p><p>e.&nbsp;&nbsp;&nbsp; pelakanaan penyiapan bahan status kepegawaian;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan penetapan masa persiapan pensiun&nbsp; pengawai negeri sipil;</p><p>g.&nbsp;&nbsp;&nbsp; pelaksanaan penetapan proses pemberian pensiun pegawai negeri sipil dan pensiun janda/duda pejabat negara serta penyelenggaraan tatausaha pensiun;</p><p>h.&nbsp;&nbsp; pelaksanaan penyiapan petunjuk teknis mengenai pengangkatan, kepangkatan dan pensiun Pegawai;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan koordinasi dengan instansi dan atau lembaga terkait lainnya di bidang mutasi kepegawaian; dan</p><p>j. &nbsp; &nbsp; pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(57, 6, 'Bidang Kinerja dan Kesejahteraan', '<p>Melakukan penyiapan bahan pembinaan disiplin dan kedudukan hukum, penyusunan perencanaan dan evaluasi kinerja, penghargaan, tanda jasa, kompensasi, cuti, administrasi norma, standar, prosedur, sistem kerja kepegawaian.</p>', '<p>a.&nbsp;&nbsp; pelaksanaan pembinaan disiplin pegawai;</p><p>b.&nbsp;&nbsp; pelaksanaan penetapan penyelesaian proses hukum pegawai;</p><p>c.&nbsp;&nbsp;&nbsp; pelaksanaan penyusunan perencanaan dan standar kinerja;</p><p>d.&nbsp;&nbsp; pelaksanaan penyusunan bahan petunjuk teknis kinerja dan evaluasi pegawai;</p><p>e.&nbsp;&nbsp;&nbsp; pelaksanaan penyusunan bahan petunjuk teknis peningkatan kesejahteraan pegawai;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; pelaksanaan penyusunan bahan petunjuk teknis pemberian penghargaan, tanda jasa dan kompensasi kepegawaian;</p><p>g.&nbsp;&nbsp;&nbsp; pelaksanaan penetapan norma, standar, prosedur dan sistem kerja kepegawaian;</p><p>h.&nbsp;&nbsp; pelaksanaan koordinasi dengan instansi dan atau lembaga terkait lainnya di bidang manajemen kinerja dan kesejahteraan; dan</p><p>i. &nbsp; &nbsp; pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Badan sesuai dengan tugas dan fungsinya.</p>'),
(58, 36, 'Kepala', '<p>memimpin dinas dan melaksanakan tugas umum dan khusus pemerintahan bidang pendidikan dayah terpadu dan salafiah, santri, sumber daya manusia, manajemen, sarana dan prasarana sesuai dengan Peraturan Perundang-undangan.</p>', '<p>&nbsp;</p>'),
