INSERT INTO `tbl_suborganisasi` (`id_suborganisasi`, `id_organisasi`, `nama_suborganisasi`, `tugas_sub`, `fungsi_sub`) VALUES
(1, 2, 'Sub Bagian Umum.', '<p>Melakukan urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan, pemeliharaan dan perpustakaan kepegawaian, organisasi, ketatalaksanaan, hukum, perundang- undangan, pelaksanaan hubungan masyarakat dan protokoler.</p>', '<p>&nbsp;</p>'),
(3, 9, 'Subbagian Penyusunan Program;', '<p>Melakukan pengumpulan data dan informasi, program kerja tahunan, jangka menengah, jangka panjang, rencana anggaran yang bersumber dari APBD, APBN, bantuan dan atau hibah luar negeri, rencana strategis, laporan akuntabilitas kinerja dan rencana kinerja;&nbsp;</p>', '<p>&nbsp;</p>'),
(4, 9, 'Subbagian Tata Usaha', '<p>Melakukan urusan ketatausahaan, rumah tangga, perpustakaan kepegawaian, organisasi, ketatalaksanaan, hukum dan perundang-undangan, pelaksanaan hubungan masyarakat dan protokoler;&nbsp;</p>', '<p>&nbsp;</p>'),
(5, 9, 'Subbagian Keuangan dan Perlengkapan.', '<p>Melakukan pengelolaan administrasi keuangan, verifikasi, perbendaharaan, pembukuan, pelaporan realisasi fisik dan keuangan, perlengkapan, aset, barang inventaris dan pemeliharaan.&nbsp;</p>', '<p>&nbsp;</p>'),
(6, 11, 'Sub Bagian Umum', '<p>Melakukan urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan, pemeliharaan dan perpustakaan kepegawaian, organisasi, ketatalaksanaan, hukum dan perundang-undangan, pelaksanaan hubungan masyarakat dan protokoler.</p>', '<p>&nbsp;</p>'),
(7, 12, 'Subbidang Bina Ideologi dan wawasan Kebangsaan', '<p>Menyiapkan bahan pelaksanaan bimbingan teknis dan supervisi bidang penerapan penghayatan dan pengamalan pancasila, karakter dan wawasan kebangsaan, bela negara, sejarah kebangsaan, kewarganegaraan, pembauran dan pelestarian Binneka Tunggal Ika.&nbsp;</p>', '<p>&nbsp;</p>'),
(8, 12, 'Subbidang Pembinaan Karakter Bangsa.', '<p>Melakukan penyiapan bahan pelaksanaan kebijakan dan koordinasi, pelaksanaan pembinaan umum, pemberian bimbingan teknis, supervisi dan pelaksanaan pemantauan, evaluasi dan pelaporan serta falisitasi bidang karakter bangsa&nbsp;</p>', '<p>&nbsp;</p>'),
(9, 13, 'Subbidang Pengembangan Budaya Politik dan Pendidikan Politik.', '<p>Melakukan penyiapan bahan perumusan kebijakan, pelaksanaan kebijakan dan koordinasi, pelaksanaan pembinaan umum, pemberian bimbingan teknis dan supervisi, dan pelaksanaan pemantauan, evaluasi dan pelaporan serta falisitasi bidang pengembangan etika dan budaya politik</p>', '<p>&nbsp;</p>'),
(10, 13, 'Sub Bidang Fasilitasi Partai Politik dan Pemilu', '<p>Kebijakan politik, falisitasi lembaga pemerintahan dan perwakilan, falisitasi kelembagaan partai politik, pendidikan, etika dan budaya politik serta fasilitasi bidang fasilitasi partai politik dan pemilu&nbsp;</p>', '<p>&nbsp;</p>'),
(11, 11, 'Sub Bagian Kepegawaian', '<figure class=\"table\"><table><tbody><tr><td>Melaksanakan urusan administrasi kepegawaian seperti menyiapkan administrasi pengusulan kenaikan gaji berkala, kenaikan pangkat, cuti, Karpeg, Karis, Karsu, Taspen, Askes, penyusunan Daftar Urut Kepangkatan (DUK), tindakan disiplin dan masa persiapan Pensiun serta pemberian tanda penghargaan bagi pegawai sesuai ketentuan dan kebutuhan dalam rangka peningkatan kesejahteraan pegawai.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(12, 11, 'Sub Bagian Keuangan', '<figure class=\"table\"><table><tbody><tr><td>Melakukan urusan pengelolaan administrasi keuangan, verifikasi, perbendaharaan, pembukuan, pelaporan realisasi fisik dan keuangan di lingkungan Badan Perencanaan Pembangunan Daerah.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(13, 14, 'Sub Bidang Pengembangan Sumberdaya Alam', '<figure class=\"table\"><table><tbody><tr><td>Melakukan perencanaan ekonomi dalam penyiapan bahan perumusan kebijakan teknis, pengkoordinasian, pembinaan, dan fasilitasi serta penyusunan perencanaan pembangunan urusan pangan, pertanian, perkebunan, peternakan, kelautan dan perikanan serta pertambangan.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(14, 14, 'Sub Bidang Pengembangan Industri Perdagangan dan Pariwisata', '<p>Melakukan perencanaan ekonomi dalam penyiapan bahan perumusan kebijakan teknis, pengkoordinasian, pembinaan, dan fasilitasi serta penyusunan perencanaan pembangunan urusan perdagangan dan perindustrian, koperasi, usaha kecil dan menengah, pariwisata&nbsp; dan kebudayaan.</p>', '<p>&nbsp;</p>'),
(15, 14, 'Sub Bidang Pengembangan Investasi dan Ketenagakerjaan.', '<figure class=\"table\"><table><tbody><tr><td>Melakukan perencanaan ekonomi dalam penyiapan bahan perumusan kebijakan teknis, pengkoordinasian, pembinaan, dan fasilitasi serta penyusunan perencanaan pembangunan urusan keuangan daerah, perizinan dan penanaman modal, ketenagakerjaan, transmigrasi, urusan zakat, infaq dan sadaqah.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(16, 15, 'Sub Bidang Infrastruktur dan Perumahan', '<figure class=\"table\"><table><tbody><tr><td>Melakukan penyiapan bahan perumusan perencanaan dan program pembangunan di bidang perumahan, permukiman informasitelematika dan persandian.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(17, 15, 'Sub Bidang Pengembangan Wilayah', '<figure class=\"table\"><table><tbody><tr><td>Melakukan bahan perumusan perencanaan pengembangan wilayah, pertanahan, perhubungan darat, laut, udara dan angkutan sungai.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(18, 15, 'Sub Bidang Lingkungan Hidup dan Kebencanaan', '<figure class=\"table\"><table><tbody><tr><td>Melakukan penyiapan bahan perumusan perencanaan dan program pembangunan di bidang lingkungan hidup, pengembangan sumberdaya air dan kebencanaan.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(19, 16, 'Sub Bidang Ketahanan Ekonomi, Sosial dan Budaya.', '<p>Melaksanakan penyiapan pelaksanaan kebijakan dan koordinasi, pelaksanaan pembinaan umum, pemberian bimbingan teknis dan supervisi, dan pelaksanaan pemantauan, evaluasi dan pelaporan serta fasilitasi bidang ketahanan ekonomi dan sosial budaya.&nbsp;</p>', '<p>&nbsp;</p>'),
(20, 16, 'Sub Bidang Organisasi Kemasyarakatan', '<p>Penyiapan pelaksanaan kebijakan dan koordinasi, pelaksanaan pembinaan umum, pemberian bimbingan teknis dan supervisi, dan pelaksanaan pemantauan, evaluasi dan pelaporan, organisasi kemasyarakatan asing serta fasilitasi bidang organisasi kemasyarakatan.&nbsp;</p>', '<p>&nbsp;</p>'),
(21, 17, 'Sub Bidang Pengembangan Sumber Daya Manusia dan Keistimewaan Aceh', '<figure class=\"table\"><table><tbody><tr><td>Melakukan koordinasi penyusunan program perencanaan pembangunan di bidang pengembangan sumber daya manusia, pendidikan, pembinaan dayah, agama, adat-istiadat, seni, pemuda dan olahraga.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(22, 17, 'Sub Bidang Pemerintahaan dan Kelembagaan', '<figure class=\"table\"><table><tbody><tr><td>Melakukan koordinasi penyusunan program perencanaan pembangunan di bidang pengembangan kelembagaan, reformasi birokrasi dan tata kelola pemerintahan, ketentraman, ketertiban serta penguatan perdamaian.</td></tr></tbody></table></figure>', '<p>&nbsp;</p>'),
(23, 18, 'Bagian Umum', '<p>Melakukan pengelolaan urusan administrasi, umum, perlengkapan, peralatan, kerumahtanggaan, program, keuangan, kepegawaian, ketatalaksanaan, hukum, perundang-undangan, pelayanan administrasi di lingkungan Sekretariat MPU Aceh.</p>', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan dan pemeliharaan;</p><p>b.&nbsp;&nbsp;&nbsp; Pembinaan&nbsp; kepegawaian, organisasi dan ketatalaksanaan;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyusunan program dan pengelolaan keuangan;</p><p>d.&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; penyusunan rancangan produk hukum dan peraturan perundang-undangan; dan</p><p>Pelaksanaan&nbsp; tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Sekretariat.&nbsp;</p>'),
(24, 19, 'Subbagian Tata Usaha dan Kepegawaian', '<p>Melakukan urusan ketatausahaan, rumah tangga, kepegawaian, organisasi, ketatalaksanaan, perpustakaan, kearsipan, hukum dan perundang-undangan.&nbsp;</p>', '<p>&nbsp;</p>'),
(25, 17, 'Sub Bidang Kependudukan dan Kesejahteraan Sosial.', '<p>Melakukan koordinasi penyusunan program perencanaan pembangunan di bidang&nbsp; kesehatan, kependudukan, sosial, pemberdayaan masyarakat, pemberdayaan perempuan dan perlindungan anak.</p>', '<p>&nbsp;</p>'),
(26, 21, 'Sub Bidang Pendanaan APBA', '<p>Merumuskan dan menyusun rencana kerja tahunan, jangka menengah, jangka panjang dan rencana anggaran pendapatan belanja yang bersumber dari APBA.</p>', '<p>&nbsp;</p>'),
(27, 3, '1.1.1. Sub Bagian Umum', '<p>Melakukan urusan hukum dan ketatalaksanaan, hukum dan peraturan perundang-ungangan, ketatausahaan, administrasi perjalanan, protokoler, dan urusan umum lainnya.&nbsp;</p>', '<p>&nbsp;</p>'),
(28, 3, '1.1.2. Sub Bagian Kepegawaian', '<p>Melakukan urusan administrasi kepegawaian, organisasi, hukum dan peraturan perundang-undangan kepegawaian.&nbsp;</p>', '<p>&nbsp;</p>'),
(29, 3, '1.1.3. Sub Bagian Rumah Tangga dan Perlengkapan', '<p>Melakukan urusan kerumahtanggaan, urusan dalam dan keamanan serta pengadaan/ pemeliharaan barang inventaris, pengelolaan asset/perlengkapan dan peralatan kantor/rumah jabatan/rumah dinas.&nbsp;</p>', '<p>&nbsp;</p>'),
(30, 21, 'Sub Bidang Pendanaan Non APBA dan Kerjasama Pembangunan', '<p>Melaksanakan koordinasi, integrasi dan sinkronisasi rencana kerja tahunan, jangka menengah, jangka panjang dan rencana anggaran pendapatan belanja yang bersumber dari APBN.</p>', '<p>&nbsp;</p>'),
(31, 22, '1.2.1. Sub Bagian Akutansi Keuangan', '<p>Melakukan pelaksanaan akuntansi keuangan sesuai dengan Standar Akuntansi Pemerintah.&nbsp;</p>', '<p>&nbsp;</p>'),
(32, 22, '1.2.2. Sub Bagian Akutansi Manajemen', '<p>Melakukan pelaksanaan akuntansi manajemen, evaluasi prosedur, pengukuran kinerja dan inventarisasi asset.&nbsp;</p>', '<p>&nbsp;</p>'),
(33, 22, '1.2.3. Sub Bagian Verifikasi dan Pelaporan', '<p>Melakukan pelaksanaan pengujian kelengkapan dokumen pembayaran dan penyusunan laporan secara berkala.&nbsp;</p>', '<p>&nbsp;</p>'),
(34, 23, '1.3.1. Sub Bagian Perencanaan dan Anggaran', '<p>Melakukan penyusunan rencana kerja, koordinasi&nbsp; anggaran baik yang bersumber dari APBA, APBN dan sumber lainnya</p>', '<p>&nbsp;</p>'),
(35, 23, '1.3.2. Sub Bagian Informasi< Komunikasi dan Kerjasama', '<p>Melakukan pelayanan informasi, komunikasi, kehumasan, pelaksanaan kerja dan pemasaran.</p>', '<p>&nbsp;</p>'),
(36, 21, 'Sub Bidang Penyusunan Program dan Pelaporan.', '<p>Menyusun rencana kerja tahunan, rencana strategis dan laporan akuntabilitas kinerja, menyusun laporan kinerja dan penyiapan bahan peraturan perundang-undangan yang berhubungan dengan tugas pokok dan fungsi</p>', '<p>&nbsp;</p>'),
(37, 23, '1.3.3. Sub Bagian Evaluasi dan Pelaporan Program', '<p>Melakukan pendataan, pemantuan,&nbsp; evaluasi dan pelaporan</p>', '<p>&nbsp;</p>'),
(38, 24, '1.4.1. Sub Bagian Administrasi Penerimaan dan Mobilisasi Dana', '<p>Melakukan pengelolaan administrasi penerimaan, pendapatan dan upaya mobilisasi dana dari berbagai sumber yang sah.</p>', '<p>&nbsp;</p>'),
(40, 24, '1.4.2. Sub Bagian Administrasi Pengeluaran', '<p>Melakukan pengelolaan administrasi pengeluaran dan pertanggungjawaban pengeluaran keuangan</p>', '<p>&nbsp;</p>'),
(41, 25, 'Sub Bidang Penelitian Ekonomi, Infrastruktur dan Kewilayahan ', '<p>Melaksanakan penelitian dan pengembangan bidang ekonomi dan prasarana wilayah.</p>', '<p>&nbsp;</p>'),
(42, 25, 'Sub Bidang Penelitian Sosial, Budaya dan Pemerintahan', '<p>Mempunyai tugas melaksanakan penelitian dan pengembangan bidang sosial budaya dan pemerintahan.</p>', '<p>&nbsp;</p>'),
(43, 25, 'Sub Bidang Inovasi Daerah', '<p>Melaksanakan penelitian dan pengembangan serta fasilitasi dan penerapan bidang inovasi dan teknologi.</p>', '<p>&nbsp;</p>'),
(44, 28, 'Sub Bidang Pengendalian dan Evaluasi APBD', '<p>Melakukan pemantauan, pengendalian dan evaluasi terhadap pelaksanaan rencana pembangunan Aceh.</p>', '<p>&nbsp;</p>'),
(45, 27, '2.1.1. Seksi Pendidikan Medis dan Non Medis', '<p>Melakukan upaya peningkatan mutu pendidikan medis dan non medis</p>', '<p>&nbsp;</p>'),
(46, 27, '2.1.2. Seksi Pelatihan Medis dan Non Medis', '<p>Melakukan upaya peningkatan mutu pelatihan medis dan non medis</p>', '<p>&nbsp;</p>'),
(47, 28, 'Sub Bidang Pengendalian dan Evaluasi Non APBD', '<p>Melakukan pengendalian dan evaluasi serta koordinasi pembinaan dan pelatihan teknis bagi Kabupaten/Kota dalam melakukan pengendalian dan evaluasi hasil rencana pembangunan.</p>', '<p>&nbsp;</p>'),
(48, 29, '2.2.1. Seksi Penelitian Medis dan Non Medis', '<p>Melakukan koordinasi&nbsp; penelitian dan pengkajian di bidang kedokteran, keperawatan dan teknologi kesehatan lainnya</p>', '<p>&nbsp;</p>'),
(49, 29, '2.2.2. Seksi Pengembangan Medis dan Non Medis', '<p>Melakukan koordinasi pengembangan dan penerapan ilmu pengetahuan dan teknologi kedokteran keperawatan dan teknologi kesehatan lainnya</p>', '<p>&nbsp;</p>'),
(50, 28, 'Sub Bidang Data dan Informasi', '<p>Melakukan penyediaan dan pengelolaan data dan informasi terkait penyusunan dan evaluasi rencana pembangunan Aceh serta koordinasi pembinaan dan pelatihan teknis&nbsp; bagi kabupaten/kota dalam mengelola data dan informasi terkait penyusunan dan evaluasi.</p>', '<p>&nbsp;</p>'),
(51, 34, 'Sub Bagian Umum dan Kepegawaian', '<p>Melakukan urusan ketatausahaan, rumah tangga, barang inventaris, aset, perlengkapan, peralatan, pemeliharaan, perpustakaan, kepegawaian, organisasi, ketatalaksanaan, hukum dan perundang-undangan, pelaksanaan hubungan masyarakat dan protokoler.</p>', '<p>&nbsp;</p>'),
(52, 32, '3.1.1. Seksi Pelayanan Spesialistis dan Rujukan', '<p>Melakukan pembinaan, koordinasi, pengawasan dan pengendalian kegiatan pelayanan medis, non medis, spesialistis, pelayanan rujukan dan tindakan medis lainnya</p>', '<p>&nbsp;</p>'),
(53, 32, '3.1.2. Seksi Pengembangan Fasilitas Medis dan Non Medis', '<p>Melakukan koordinasi perencanaan kebutuhan, pemanfaatan, pemeliharaan dan pengembangan fasilitas pelayanan medis.&nbsp;</p>', '<p>&nbsp;</p>'),
(54, 35, '3.2.1. Seksi Ketenagaan dan Etika Profesi', '<p>Melakukan pembinaan dan pengendalian ketenagaan, penerapan etika dan peningkatan profesionalisme tenaga keperawatan.</p>', '<p>&nbsp;</p>'),
(55, 35, '3.2.2. Seksi Asuhan Keperawatan', '<p>Melakukan koordinasi, pembinaan, pengawasan, pengendalian dan penilaian&nbsp; mutu pelayanan&nbsp; asuhan keperawatan.&nbsp;</p>', '<p>&nbsp;</p>'),
(56, 37, '4.1.1. Seksi Penunjang Medis', '<p>Melakukan pengumpulan dan pengolahan data, analisa kebutuhan, pengembangan sarana dan prasarana pelayanan penunjang medis serta penyiapan bahan pembinaan, koordinasi, pengawasan dan pengendalian kegiatan pelayanan penunjang medis.</p>', '<p>&nbsp;</p>'),
(57, 37, '4.1.2. Seksi Penunjang Non Medis', '<p>Melakukan pengumpulan, pengolahan, dan analisa kebutuhan, pengembangan sarana dan prasarana penunjang non medis serta penyiapan bahan pembinaan, koordinasi, pengawasan dan pengendalian pelayanan penunjang non medis.&nbsp;</p>', '<p>&nbsp;</p>'),
(58, 34, 'Sub Bagian Penyusunan Program', '<p>Melaksanakan penyusunan program kerja tahunan, jangka menengah, jangka panjang, rencana anggaran yang bersumber dari Anggaran Pendapatan dan Belanja Aceh, Anggaran Pendapatan dan Belanja Negara, rencana strategis, laporan akuntabilitas kinerja dan rencana kerja tahunan Badan.</p>', '<p>&nbsp;</p>'),
(59, 38, '4.2.1. Seksi Logistik Medis dan Non Medis', '<p>Melakukan penyiapan rencana distribusi logistik fasilitas pelayanan penunjang medis dan non medis dan penyiapan bahan pembinaan, koordinasi dan pengendalian penggunaan logistik medis/non medis.</p>', '<p>&nbsp;</p>'),
(60, 38, '4.2.2. Seksi Pemeliharaan Fasilitas', '<p>Melakukan upaya pendataan, pemeliharaan fasilitas pelayanan penunjang medis/ non medis dan penyiapan bahan pembinaan dan koordinasi pemeliharaan fasilitas penunjang medis/ non medis.</p>', '<p>&nbsp;</p>'),
(61, 34, 'Sub Bagian Keuangan', '<p>Melakukan pengelolaan administrasi keuangan, verifikasi, perbendaharaan, pembukuan, pelaporan realisasi fisik dan keuangan serta&nbsp; penyusunan laporan keuangan di lingkungan Badan.</p>', '<p>&nbsp;</p>'),
(62, 39, 'Sub Bidang Keistimewaan Aceh dan Sumberdaya Manusia', '<p>a.&nbsp;&nbsp; Menyusun anggaran pendapatan dan belanja daerah</p><p>b.&nbsp;&nbsp; Menyiapkan bahan pembinaan teknik pengelolaan anggaran pendapatan dan pembiayaan daerah</p><p>c.&nbsp;&nbsp;&nbsp; Mengkaji ulang hasil verifikasi anggaran pendapatan dan pembiayaan daerah</p><p>d.&nbsp;&nbsp; Menyusun anggaran belanja daerah</p><p>e.&nbsp;&nbsp;&nbsp; Menyiapkan bahan pembinaan teknis pengelolaan anggaran belanja daerah</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Mengkaji ulang hasil verifikasi anggaran belanja daerah</p><p>g.&nbsp;&nbsp;&nbsp; Melaksanakan penyiapan petunjuk teknis penyusunan rancangan APBA dan rancangan perubahan APBA</p><p>h.&nbsp;&nbsp; Melaksanakan penyusunan dan pembahasan RBA/RKA SKPA dan PPKA</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan koordinasi dan kompilasi bahan-bahan penyusunan jawaban eksekutif dalam rangka penyusunan Rancangan Qonun tentang APBA dan Rancangan Qonun tentang Perubahan APBA</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan penyempurnaan Rancangan Qonun tentang APBA dan Rancangan Qonun tentang Perubahan APBA</p>', '<p>&nbsp;</p>'),
(63, 39, 'Sub Bidang Ekonomi dan Ketenagakerjaan', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp; menyusun anggaran pendapatan dan pembiayaan daerah;</p><p>b.&nbsp;&nbsp;&nbsp; menyiapkan bahan pembinaan teknis pengelolaan anggaran pendapatan dan pembiayaan daerah;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; mengkaji ulang hasil verifikasi anggaran pendapatan dan pembiayaan daerah;</p><p>d.&nbsp;&nbsp;&nbsp; menyusun anggaran belanja daerah;</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp; menyiapkan bahan pembinaan teknis pengelolaan anggaran belanja daerah;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengkaji ulang hasil verifikasi anggaran belanja daerah;</p><p>g.&nbsp;&nbsp;&nbsp; melaksanakan penyiapan petunjuk teknis penyusunan rancangan APBA dan rancangan Perubahan APBA;</p><p>h.&nbsp;&nbsp;&nbsp; melaksanakan penyusunan dan pembahasan RBA/RKA SKPA dan PPKA;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan koordinasi dan kompilasi bahan-bahan penyusunan jawaban eksekutif dalam rangka penyusunan Rancangan Qanun tentang APBA dan Rancangan Qanun tentang Perubahan APBA;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyempurnaan Rancangan Qanun tentang APBA dan Rancangan Qanun tentang Perubahan APBA;</p><p>k.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyiapan penetapan Rancangan Qanun tentang APBA dan&nbsp; Rancangan Qanun tentang Perubahan APBA;</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyiapan bahan persetujuan dan pengesahan DPA/DPPA SKPA, BLUD dan PPKA;</p><p>m.&nbsp;&nbsp; melaksanakan koordinasi penyusunan anggaran SPKA, BLUD dan PPKA;</p><p>n. &nbsp; &nbsp;melaksanakan koordinasi penyusunan anggaran pendapatan dan pendapatan PPKA</p>', '<p>&nbsp;</p>'),
(64, 39, 'Sub Bidang Sarana dan Prasarana', '<p>a.&nbsp;&nbsp; menyusun anggaran pendapatan dan pembiayaan daerah</p><p>b.&nbsp;&nbsp; menyiapkan bahan pembinaan teknis pengelolaan anggaran pendaptan dan epmbiayaan daerah</p><p>c.&nbsp;&nbsp;&nbsp; mengkaji ulang hasil verifikasi anggaran pendapatan dan pembiayaan daerah</p><p>d.&nbsp;&nbsp; menyusun anggaran belanja daerah</p><p>e.&nbsp;&nbsp;&nbsp; menyiapkan bhan pembinaan teknis pengelolaan anggaran belanja daerah</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; mengkaji ulang hasil verifikasi anggaran belanja daerah</p><p>g.&nbsp;&nbsp;&nbsp; melaksanakan penyiapan petunjuk teknis penyusunan rancangan APBA dan rancangan Perubahan APBA</p><p>h.&nbsp;&nbsp; melaksanakan penyusunan dan pembahasan RBA./RKA SKPA dan PPKA</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan koordinasi dan kompilasi bahan-bahan penyusunan jawaban eksekutif dalam rangka penyusunan Rancngan Qanun tentang APBA dan Rancangan Qanun tentang Perubahan APBA</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyempurnaan Rancangan Qanun tentang tentang APBA dan Rancangan Qanun tentang Perubahan APBA</p><p>k.&nbsp;&nbsp; melaksanakan penyiapan penetapan Rancangan Qanun tentang tentang APBA dan Rancangan Qanun tentang Perubahan APBA</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyaiapan bahan persetujuan dan pengesahan DPA/DPPA SKPA, BLUD dan PPKA</p><p>m. melaksanakan pembinaan penyusunan anggaran SKPA, BLUD dan PPKA</p><p>n. &nbsp; &nbsp;melaksanakan koordinasi penyusunan anggaran pendapatan daerah dan pendapatan PPKA</p>', '<p>&nbsp;</p>'),
(65, 39, '', '', ''),
(66, 40, 'Sub Bidang Penatausahaan Barang Milik Aceh', '<p>a.&nbsp;&nbsp; melaksanakan dan menyusun program kerja subbidang penatausahan barang milik Aceh</p><p>b.&nbsp;&nbsp; melaksanakan pengkajian dan penyusunan bahan kebijakan teknis perencanaan kebutuhan, inventarisasi, revaluasi dan penatausahaan barang milik Aceh</p><p>c.&nbsp;&nbsp;&nbsp; melaksanakan perencanaan kebutuhan, inventarisasi, revaluasi dan penetausahan barang milik Aceh</p><p>d.&nbsp;&nbsp; melaksanakan pengkajian dan penyusunan bahan kebiakan teknis revaluasi barang milik Aceh</p><p>e.&nbsp;&nbsp;&nbsp; melaksanakan pengkajian dan penyusunan bahan kebiakan teknis inventarisasi barang milik Aceh</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan koordinasi dan fasilitasi penyelenggaraaan pencatatan barang milik Aceh</p><p>g.&nbsp;&nbsp;&nbsp; melaksnakan koordinasi dan fasilitasi penyelenggaraan rekonsiliasi barang milik Aceh</p><p>h.&nbsp;&nbsp; melaksnakan koordinasi dan fasilitasi penyelenggaraan penyusunan laporan barang milik Aceh</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; melaksnaakan penyusunan dan pengelolaan data informasi penatausahaan barang milik Aceh</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; melaksnakan koordinasi dan fasilitasi penyelenggaraan penyusunan Rencana Kebutuhan barang milik Aceh</p><p>k.&nbsp;&nbsp; melaksnakan penyusunan dan pengelolaan data informasi rencana kebutuhan barang milik Aceh</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan koordinasi dan fasilitasi penyelenggaraan revaluasi barang milik Aceh</p><p>m. menyusun dan mengelola data informasi revaluasi barang milik Aceh</p><p>n.&nbsp;&nbsp; melaksanakan koordinasi dan fasilitasi penyelenggaraan inventarisasi barang milik Aceh</p><p>o.&nbsp;&nbsp; menyusun dan mengelola data ifnormasi&nbsp; inventarisasi barang milik Aceh</p><p>p.&nbsp;&nbsp; menyusun bahan tindak lanjut laporan hasil pemeriksaan lingkup penatausahaan, revaluasi dan rencana kebutuhan barang milik Aceh</p><p>q.&nbsp;&nbsp; melaksnaakan pembinaan penyelenggaaan penetausahaan, inventarisasi, revaluasi dan rencana kebutuhan barang m', '<p>&nbsp;</p>'),
(67, 40, 'Sub Bidang Legalitas dan Pengamanan Barang Milik Aceh', '<p>a.&nbsp;&nbsp; melaksanakan dan menyusun program kerja Subbidang legalitas dan pengamanan barang milik Aceh</p><p>b.&nbsp;&nbsp; melaksanakan pengakjaian dan penyusunan bahan kebijakan teknis legalisasi, penyimpanan bukti kepemilikan, pengamanan dan pemeliharaan serta penghapusan barang milik Aceh</p><p>c.&nbsp;&nbsp;&nbsp; melaksanakan legalisasi, penyimpanan bukti kepemilikan, pengamanan dan pemeliharaan serta penghapusan barang milik Aceh</p><p>d.&nbsp;&nbsp; melaksanakan koordinasi dan faslitasi penyelenggaran legalisasi barang milik Aceh</p><p>e.&nbsp;&nbsp;&nbsp; melaksanakan pengumpulan bukti kepemilikan barang milik Aceh</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyimpanan bukti kepemilikan barang milik Aceh</p><p>g.&nbsp;&nbsp;&nbsp; menyusun dan mengelola data informasi legalitas bukti barang milik Aceh</p><p>h.&nbsp;&nbsp; melaksanakan koordinasi dan fasilitasi penyelenggaraan pengamanan dan pemeliharaan barang milik Aceh</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; menyusun dan mengelola data informasi pengamanan dan pemeliharaan barang milik Aceh</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan koordinasi dan fasilitasi penyelenggaraan penghapusan barang milik Aceh</p><p>k.&nbsp;&nbsp; menyusun dan mengelola data informasi penghapusan barang milik Aceh</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; menyusun bahan tindak lanut laporan hasil pemeriksaan lingkup legalitas, pengamanan dan penghapusan barang milik Aceh</p><p>m. &nbsp; &nbsp;melaksanakan monitoring, evaluasi dan pelaporan kegiatan subbidang&nbsp;</p>', '<p>&nbsp;</p>'),
(68, 18, 'Bagian Hukum dan Komunikasi Publik', '<p>Melaksanakan penyiapan bahan pengkajian bidang hukum dan peraturan perundang-undangan, fatwa, sistem jaminan produk halal dan komunikasi publik serta fasilitasi Badan Otonom</p>', '<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Penyiapan&nbsp; bahan perumusan rancangan qanun, sosialisasi fatwa, dan keputusan Pimpinan MPU;</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp; Pengumpulan&nbsp; dan pengelolaan data pengkajian terhadap rancangan qanun, fatwa MPU, Keputusan MPU, Keputusan Pimpinan MPU;</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; fasilitasi bantuan hukum kepada semua unsur terkait di lingkungan MPU;</p><p>4.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; fasilitasi Badan Otonom;</p><p>5.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; fasilitasi dan sertifikasi sistem jaminan produk halal;</p><p>6.&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; pelayanan informasi, komunikasi publik/ kehumasan, pelaksanaan tata upacara, keprotokolan dan dukumentasi; dan</p><p>f. pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Sekretariat MPU.&nbsp;</p>'),
(69, 18, 'Bagian Kajian Strategis, Risalah dan Perundangan', '<p>Menyiapkan bahan dalam rangka pembahasan atau pengkajian strategis, kerjasama, risalah dan persidangan.</p>', '<p>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; fasilitasi pengkajian strategis;</p><p>2.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; penyiapan bahan pertimbangan MPU;</p><p>3.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan&nbsp; fasilitasi dan pengoordinasian tenaga ahli;</p><p>4.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan hubungan kerjasama dengan institusi dan/atau lembaga terkait;</p><p>5.&nbsp;&nbsp;&nbsp;&nbsp; Pelaksanaan penyusunan risalah dan taushiah;</p><p>6.&nbsp;&nbsp;&nbsp; Pengumpulan bahan pembuatan risalah rapat, sidang dan penyiapan urusan administrasi persidangan;</p><p>7.&nbsp;&nbsp;&nbsp;&nbsp; Penyiapan persidangan dan tata tempat;</p><p>8.&nbsp;&nbsp;&nbsp; Penyiapan daftar resume, notulensi dan laporan hasil rapat dan sidang MPU;</p><p>9.&nbsp;&nbsp;&nbsp; Pelaksanaan koordinasi dengan instansi dan atau lembaga terkait dalam rangka mendukung kelancaran kegiatan rapat, persidangan dan risalah; dan</p><p>10.&nbsp; Pelaksanaan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Sekretariat.</p>'),
(70, 18, 'Kelompok Jabatan Fungsional', '<p>Melaksanakan sebagian tugas Pemerintah Aceh sesuai dengan keahlian dan kebutuhan.</p>', '<p>&nbsp;</p>'),
(71, 19, 'Subbagian Program', '<p>Melakukan penyusunan program dan rencana kerja, penyusunan program kerja tahunan, jangka menengah, jangka panjang, pendataan, penelitian dan pengembangan, pemantauan, evaluasi dan pelaporan serta penyusunan akuntabilitas kinerja.&nbsp;</p>', '<p>&nbsp;</p>'),
(72, 19, 'Subbagian Keuangan ', '<p>Melakukan penatausahaan administrasi keuangan, barang inventaris, aset, perlengkapan, peralatan dan pemeliharaan.&nbsp;</p>', '<p>&nbsp;</p>'),
(73, 41, 'Sub Bagian Hukum dan Fatwa', '<p>Melakukan penyiapan dan pengumpulan bahan pengkajian dan perumusan rancangan qanun, sosialisasi fatwa, keputusan Pimpinan MPU dan fasilitasi Badan Otonom dan bantuan hukum kepada semua unsur terkait di lingkungan MPU.&nbsp;</p>', '<p>&nbsp;</p>'),
(74, 41, 'Sub Bagian Fasilitasi Setifikasi Halal', '<p>Melakukan fasilitasi sertifikasi dan sistem jaminan produk halal.&nbsp;</p>', '<p>&nbsp;</p>'),
(75, 41, 'Sub Bagian Komuniasi Publik', '<p>Melakukan pelayanan informasi, komunikasi publik/ kehumasan, pelaksanaan tata upacara, keprotokolan dan dukumentasi.&nbsp;</p>', '<p>&nbsp;</p>'),
(76, 42, 'Sub Bagian Kajian Strategis', '<p>Melakukan fasilitasi pengkajian strategis, penyiapan bahan pertimbangan MPU, pelaksanaan fasilitasi dan pengoordinasian tenaga ahli dan pelaksanaan hubungan kerjasama dengan institusi dan/atau lembaga terkait.&nbsp;</p>', '<p>&nbsp;</p>'),
(77, 42, 'Sub Bagian Risalah ', '<p>Melakukan penyusunan risalah dan taushiah, pengumpulan bahan pembuatan risalah rapat.&nbsp;</p>', '<p>&nbsp;</p>'),
(78, 42, 'Sub Bagian Persidangan', '<p>Melakukan pelayanan persidangan, persiapan rapat/sidang dan tempat, penyiapan daftar resume dan notulensi serta laporan hasil rapat.&nbsp;</p>', '<p>&nbsp;</p>'),
(79, 44, 'Sub Bag Umum dan Ketatalaksanaan', '<p>Melakukan urusan ketatausahaan, rumah tangga, perlengkapan, ketatalaksanaan, hokum dan perundang-undangan, perpustakaan dan kearsipan.&nbsp;</p>', '<p>&nbsp;</p>'),
(80, 44, 'Sub Bag Kepegawaian', '<p>Melakukan urusan kepegawaian</p>', '<p>&nbsp;</p>'),
(81, 44, 'Sub Bag Keuangan, Program dan Evaluasi', '<p>Melakukan urusan keuangan, perencanaan dan penyusunan program, hubungan masyarakat, data, informasi, monitoring, evaluasi dan pelaporan.&nbsp;</p>', '<p>&nbsp;</p>'),
(82, 45, 'Seksi Perencanaan, Pengaturan Penguasaan dan Penggunaan Tanah', '<p>Menyiapkan bahan-bahan pelaksanaan konsolidasi tanah, penegasan obyek pengaturan penguasaan tanah, penyelenggaraan sidang <i>land reform </i>tingkat provinsi, retribusi tanah, tanah ulayat dan tanah khas gampong/desa, inventarisasi dan pemanfaatan dan penyelesaian tanah kosong dan tanah terlantar.&nbsp;</p>', '<p>&nbsp;</p>'),
(83, 45, 'Seksi Inventarisasi dan Pengendalian Penguasaan & Penggunaan Tanah', '<p>Melakukan inventarisasi dan pengendalian penguasaan dan penggunaan tanah.</p>', '<p>&nbsp;</p>'),
(84, 45, 'Seksi Informasi dan Pendataan P4T', '<p>Melakukan penyebarluasan informasi dan pendataan Penguasaan, Pemilikan, Penggunaan dan Pemanfaatan Tanah (P4T).&nbsp;</p>', '<p>&nbsp;</p>'),
(85, 46, 'Seksi Penyelenggaran Pengadaan Tanah', '<p>Menyelenggarakan pengadaan tanah.</p>', '<p>&nbsp;</p>'),
(86, 46, 'Seksi Pengurusan Hak Atas Tanah Instansi Pemerintah', '<p>Melakukan pengurusan hak atas tanah instansi pemerintah</p>', '<p>&nbsp;</p>'),
(87, 46, 'Seksi Pengurusan Hak atas Tanah Perorangan dan Badan Hukum', '<p>Melakukan pengurusan hak atas tanah perorangan dan badan hukum.</p>', '<p>&nbsp;</p>'),
(88, 47, 'Seksi Fasilitas Penyelesaian Sengketa dan Konflik Pertanahan', '<p>Melakukan fasilitasi penyelesaian sengketa dan konflik pertanahan.</p>', '<p>&nbsp;</p>'),
(89, 47, 'Seksi Penyuluhan, Advokasi, Hukum dan Pemasyarakatan Peraturan Perundang-undangan Pertanahan', '<p>Melaksanakan penyuluhan, advokasi hukum dan pemasyarakatan peraturan perundang-undangan pertanahan.</p>', '<p>&nbsp;</p>'),
(90, 47, 'Seksi Pembinaan dan Kerjasama Perangkat/Lembaga/Aparatur Pertanahan dan Kabupaten/Kota', '<p>Melakukan pembinaan dan kerjasama perangkat/lembaga/aparatur pertanahan dan kabupaten/kota</p>', '<p>&nbsp;</p>'),
(91, 40, 'Sub Bidang Pemindahan Barang Milik Aceh', '<p>a.&nbsp;&nbsp; menyusun program kerja subbidang pemindahan barang milik Aceh</p><p>b.&nbsp;&nbsp; melaksanakan pengkajian dan penyusunan bahan kebijakan teknis pemindahtangan barang milik Aceh</p><p>c.&nbsp;&nbsp;&nbsp; melaksanakan pemindahtangan dan pemusnahan barang milik Aceh</p><p>d.&nbsp;&nbsp; melaksanakan koordinasi dan fasilitasi penyelenggarakan pemindahantangan barang milik Aceh</p><p>e.&nbsp;&nbsp;&nbsp; melaksankan pengkajian dan penyusunan bahan kebijakan teknis pemusnahan barang milik Aceh</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; melaksankan koordinasi dan fasilitasi penyelengaraan pemusnahan barang milik Aceh</p><p>g.&nbsp;&nbsp;&nbsp; menyusun dan mengelola data informasi pemindahtangan barang milik Aceh</p><p>h.&nbsp;&nbsp; menyusun dan mengelola data informasi pemusnahan barang milik Aceh</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan pembinaan teknis penyelenggaraan pemindahtangan dan pemusnahan barang milik Aceh</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; menyusun bahan tindak lanjut laporan hasil pemeriksanaan lingkup pemindahtangan dan pemusnahan barang milik Aceh</p><p>k. &nbsp; &nbsp; melaksanakan monitoring, evaluasi dan pelaporan kegiatan subbidang.</p>', '<p>&nbsp;</p>'),
(92, 48, 'Sub Bidang Pendapatan Asli Aceh', '<p>Melakukan pengumpulan, pengolahan dan pencatatan penerimaan pajak daerah, restribusi daerah, hasil pengelolaan kekayaan daerah yang dipisahkan milik Aceh, zakat dan lain-lain pendapatan asli Aceh.</p>', '<p>&nbsp;</p>'),
(93, 48, 'Sub Bidang Dana Perimbangan dan Lain Pendapatan Yang Sah', '<p>Melakukan pencatatan penerimaan non pendapatan asli Aceh, dana otonomi khusus dan lain-lain pendapatan yang sah, menyiapkan bahan koordinasi dan konsultasi dengan pemerintah pusat dalam rangka penerimaan Aceh.</p>', '<p>&nbsp;</p>'),
(94, 48, 'Sub Bidang Pengembangan Pendapatan Asli Aceh.', '<p>Melakukan pengkajian pengembangan potensi pajak daerah unggulan, restribusi daerah unggulan, hasil pengelolaan kekayaan daerah yang dipisahkan milik Aceh, zakat dan lain-lain pendapatan asli daerah.</p>', '<p>&nbsp;</p>'),
(95, 49, 'Sub Bidang Perbendaharaan I', '<p>a.&nbsp;&nbsp; menyusun rencana kerja berdasarkan peraturan perundang-undangan</p><p>b.&nbsp;&nbsp; menyusun dan merumuskan rancangan kebijakan teknis di bidang perbendaharaan</p><p>c.&nbsp;&nbsp;&nbsp; melaksanakan pengeolaan kas daerah</p><p>d.&nbsp;&nbsp; melaksanakan pemindahbukuan kas daerah</p><p>e.&nbsp;&nbsp;&nbsp; melaksanakan pengkajian ulang hasil verifikasi pengelolaan kas daerah</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan pengkajian ulang hasil verifikasi pemindahbukuan kas daerah</p><p>g.&nbsp;&nbsp;&nbsp; melaksanakan penelitian dokumen atau bukti penerimaan uang daerah dan penatausahaan dana transfer daerah sesuai dengan rekening kas umum daerah</p><p>h.&nbsp;&nbsp; melaksanakan pembukuan dan pengadministrasian penerimaan dan pengeluaran daerah</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; melaksnakan pemeriksaan, analisis da nevaluasi pertanggunjaawaban pendaptan/penerimaan dana</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; menyusun laporan penerimaan dan pengeluaran kas serta rekonsiliasi data penerimaan kas dengan instansi terkait dalam rangka penyusunan posisi kas</p><p>k.&nbsp;&nbsp; melaksanakan analisis pemberdayaan dan penempatan uang daerah sebagai optimalisasi kas dalam rangka penerimaan daerah</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan perumusan petunjuk teknis administrasi keuangang yang berkaitan dengan penatausahaan penerimaan kas</p><p>m. melaksanakan pengelolaan utang/pinjaman dan piutang daerah</p><p>n.&nbsp;&nbsp; menyusun dan menyediakan laporan aliran kas secara periodik</p><p>o.&nbsp;&nbsp; menyusun, mengoreksi dan menyempurnakan konsep naskah dinas yang terkait dengan pengelolaan kas</p><p>p.&nbsp;&nbsp; melaksanakan pengecekan pemrosesan restitusi/pengembalian kelebihan penerimaan</p><p>q.&nbsp;&nbsp; melaksanakan penyusunan dan pembuatan laporan realisasi pengeluaran kas berdasarkan Surat Perintah Pencairan Dana</p><p>r.&nbsp;&nbsp;&nbsp; menyusun kebijakan dan pedoman pelaksanan APBA terkait pendapatan daerah</p><p>s.&nbsp;&nbsp;&nbsp; memanta', '<p>&nbsp;</p>'),
(96, 49, 'Sub Bidang Perbendaharaan II', '<p>a.&nbsp;&nbsp; menyusun dan membuat rencana kerja berdasarkan peraturan perundang-undangan;</p><p>b.&nbsp;&nbsp; menyusun dan mengendalikan perumusan rancangan kebijakan teknis di bidang perbendaharaan</p><p>c.&nbsp;&nbsp;&nbsp; melaksanakan penatausahaan pembiayaan daerah (perbendaharaan)</p><p>d.&nbsp;&nbsp; mengkaji ulang hasil verifikasi penatausahaan pembiayaan daerah (perbendaharaan)</p><p>e.&nbsp;&nbsp;&nbsp; melaksanakan register SPM dan SP2D atas SKPS, pengendalian atas pagu anggaran dan penelitian dokumen SPM</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan proses penerbitan SP2D dan pendistribusian lembar SP2D</p><p>g.&nbsp;&nbsp;&nbsp; meneliti, mengoreksi dan memberikan persetujuan pembebanan rincian penggunaan atas pengesahan SPJ gaji dan non gaji</p><p>h.&nbsp;&nbsp; melaksankan pengadministrasian pemungutan dan pemotongan Perhitungan Pihak Ketiga (PPK) dan rekonsiliasi pengeluaran kas berdasarkan SP2D dengan SKPA dan instansi terkait dalam rangka pengendalian kas</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; melaksankan penyusunan dan pembuatan laporan realisasi pengeluaran kas berdasarkan SP2D</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; meneliti dan mengoreksi kelengkapan dokumen SKPP serta melaksanakan proses penrbitan SKPP</p><p>k.&nbsp;&nbsp; melakukan pembinaanterhadap SKPA dalam hal melaksankan perbendaharaan</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; melaksankan penerbitan Surat Penyediaan Dana (SPD) Belanja Langsung dan Belanja Tidak Langsung SKPA dan PPKA</p><p>m. merumuskan petunujuk teknis administrasi keuangan yang berkaitan dengan pengeluaran kas dan pertanggung jawaban (SPJ)</p><p>n.&nbsp;&nbsp; menysun, mengoreksi dan menyempurnakan konsep naskah dinas yang terkait dengan perbendaharaan belanja</p><p>o.&nbsp;&nbsp; menyusun kebijakan dan pedoman pelaksanaan APBA terkait belanja daerah; dan</p><p>p. &nbsp; melakukan pembayaran berdasarkan permintaan Pejabat Pengguna Anggaran/Kuasa Pengguna Anggaran atas beban rekening kas umum daerah.</p>', '<p>&nbsp;</p>'),
(97, 49, 'Sub Bidang Perbendaharaan III', '<p>a.&nbsp;&nbsp; menyusun dan membuat rencana kerja berdasarkan peraturan perundang-undangan</p><p>b.&nbsp;&nbsp; menyusun dan mengendalikan perumusan rancangan kebijakan teknis di bidang perbendaharaan</p><p>c.&nbsp;&nbsp;&nbsp; melaksanakan pembinaan teknis pengelolan kas daerah</p><p>d.&nbsp;&nbsp; melaksanakan bimbingan teknis pembinaan perbendaharaan</p><p>e.&nbsp;&nbsp;&nbsp; melaksanakan register SPM dan SP2Da atas SKPA, pengendalian atas pagu anggaran dan penelitian dokumen SPM</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan proses penerbitan SP2D dan pendistribusian lembar SP2D</p><p>g.&nbsp;&nbsp;&nbsp; meneliti, mengoreksi dan memberikan persetujuan pembebanan rincian penggunaan atas pengesahan SPJ gaji dan non gaji</p><p>h.&nbsp;&nbsp; melaksanakan pengadministrasian pemungutan dan pemotongan Perhitungan Pihak Ketiga (PPK) dan rekonsiliasi pengeluaran kas berdasarkan SP2D dengan SKPA dan instansi terkait dalam rangka pengendalian kas</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyusunan dan pembuatan laporan realisasi pengeluaran kas berdasarkan SP2D</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp; meneliti dan mengoreksi kelengkapan dokumen SKPP serta melaksanakan proses penerbitan SKPP</p><p>k.&nbsp;&nbsp; melakukan pembinaan terhadap SKPA dalam hal pelaksanaan perbendaharaan</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penerbitan Surat Penyediaan Dana (SPD) Belanja Langsung dan Belanja Tidak Langsung SKPA dan PPKA</p><p>m. merumuskan petunjuk teknis administrasi keuangan yang berkaitan dengan pengeluaran kas dan pertanggung jawaban (SPJ)</p><p>n.&nbsp;&nbsp; menyusun, mengoreksi dan menyempurnakan konsep naskah dinas yang terkait dengan perbendaharaan belanja</p><p>o.&nbsp;&nbsp; menyusun kebijakan dan pedoman pelaksanaan APBA terkait belanja daerah</p><p>p.&nbsp;&nbsp; melakukan pembayaran berdasarkan permintaan Pejabat Pengguna Anggaran/Kuasa Pengguna Anggaran atas beban rekening kas umum daerah</p><p>q.&nbsp;&nbsp; melaksanakan monitoring, evaluasi dan pelapora', '<p>&nbsp;</p>'),
(98, 50, 'Sub Bidang Akuntansi I', '<p>a.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksanakan penyusunan rencana kerja pada lingkup&nbsp; subbidangtugasnya</p><p>b.&nbsp;&nbsp;&nbsp; Menyiapkan bahan penyusunan kebijakan dan pedoman teknis operasional terkait penyelenggaraan akutansi</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan pencatatan dan pelaksanaan system akutansi pendapatan, beban dan belanja, pembiayaan, asset, kewajiban serta ekuitas pada satuan perangkat kerja Aceh yang menjadi lingkup tugasnya.</p><p>d.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksanakan pengumpulan serta pengolahan basis data dalam pelaksanaan system akutansi sebagai bahan input data realisasi pendapatan, belanja dan pembiayaan</p><p>e.&nbsp;&nbsp;&nbsp; Menyusun rekapitulasi harian hasil pengolahan basis data</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan rekonsialisasi antara pencatatan kas dan bank pada satuan Kerja Perangkat Aceh yang menjadi lingkup tugasnya dengan pencatatan kas dan bank pada rekening kas umum Aceh</p><p>g.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksanakan rekonsialisasi pendapatan, belanja dan pembiayaan dengan satuan perangkat kerja Aceh serta menuangkan hasil rekonsialisasi dalam berita acara rekonsialisasi</p><p>h.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksanakan rekonsialisasi asset tetap dengan Satuan Kerja Perangkat Aceh pada lingkup subbidang tugasnya serta menuangkan hasil rekonsialisasi dalam berita acara rekonsialisasi; dan</p><p>i. &nbsp; &nbsp; &nbsp;Melakukan kompilasi pencatatan system informasi keuangan pada Satuan Kerja Perangkat Aceh yang menjadi lingkup tugasnya.</p>', '<p>&nbsp;</p>'),
(99, 50, 'Sub Bidang Akuntansi II', '<p>a.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksankan penyusunan rencana kerja</p><p>b.&nbsp;&nbsp;&nbsp; Menyiapkan bahan penyusunan kebijakan dan pedoman teknis operasional terkait penyelenggaraan akutansi</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan pencatatan dan pelaksanaan system akutansi pendapatan, beban dan belanja, pembiayaan, asset, kewajiban serta ekuitas pada Satuan Kerja Perangkat Aceh</p><p>d.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksankan pengumpulan serta pengolahan basis data dalam pelaksanaan system akutansi sebagai bahan input data realisasi pendapatan, belanja dan pembiayaan pada lingkup subbidangnya</p><p>e.&nbsp;&nbsp;&nbsp; Menyusun rekapitulasi harian hasil pengolahan basis data pada lingkup subbidang tugasnya</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan rekonsialisasi antara pencatatan kas dan bank pada satuan Kerja Perangkat Aceh yang menjadi lingkup tugasnya dengan pencatatan kas dan bank pada rekening kas umum Aceh</p><p>g.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksanakan rekonsialisasi pendapatan, belanja dan pembiayaan dengan satuan perangkat kerja Aceh serta menuangkan hasil rekonsialisasi dalam berita acara rekonsialisasi</p><p>h.&nbsp;&nbsp;&nbsp; Menyiapkan bahan dan melaksanakan rekonsialisasi asset tetap dengan Satuan Kerja Perangkat&nbsp; Aceh pada lingkup subbidang tugasnya serta menuangkan hasil rekonsialisasi dalam berita acara rekonsialisasi; dan</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melakukan kompilasi pencatatan system informasi keuangan pada Satuan Kerja Perangkat Aceh yang menjadi lingkup tugasnya.</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksanakan monitoring, evaluasi dan pelaporan kegiatan subbidang; dan</p><p>k. &nbsp; &nbsp; Melaksanakan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Bidang sesuai dengan tugas dan fungsinya.</p>', '<p>&nbsp;</p>'),
(100, 50, 'Sub Bidang Akuntansi III', '<p>a.&nbsp;&nbsp;&nbsp; menyiapkan bahan dan melaksanakan penyusunan rencana kerja pada lingkup subbidang tugasnya</p><p>b.&nbsp;&nbsp;&nbsp; menyiapkan bahan penyusunan kebijakan dan pedoman teknis operasional terkait penyelenggaraan akuntansi</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; melakukan pencatatan dan pelaksanaan sistem akuntansi pendapatan, beban dan belanja, pembiayan, aset, kewajiban, serta ekuitas pada Satuan Kerja Perangkat Aceh yang menjadi lingkup tugasnya</p><p>d.&nbsp;&nbsp;&nbsp; menyiapkan bahan dan melaksanakan pengumpulan serta pengolahan basis data dalam pelaksanaan sistem akuntansi sebagai bahan input data realiasis pendapatan, belana dan pembiayaan pada lingkup tugasnya</p><p>e.&nbsp;&nbsp;&nbsp; menyusun rekapitulasi harian hasil pengolahan basis data pada lingkup subbidang tugasnya</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; melakukan rekonsiliasi antara pencatatan kas dan bank pada Satuan Kera Perangkat Aceh yang menjadi lingkup tugasnya dengan pencatatan kas dan bank pada rekening kas umum Aceh</p><p>g.&nbsp;&nbsp;&nbsp; menyiapkan bahan dan melaksanakan bahan dan melaksanakan rekonsiliasi pendapatan, belanja dan pembiayaan dengan Satuan Kera Perangkat Aceh yang menjadi lingkup tugasnya serta menuangkan hasil rekonsiliasi dalam berita acara rekonsiliasi</p><p>h.&nbsp;&nbsp;&nbsp; menyiapkan bahan dan melaksanakan rekonsiliasi aset tetap dengan Satuan Kerja Perangkat Aceh yang menjadi lingkup tugasnya serta menuangkan hasil rekonsiliasi dalam berita acara rekonsiliasi</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melakukan kompilasi pencatatan sistem informasi keuangan pada satuan kerja perangkat Aceh yang menjadi lingkup tugasnya</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan monitoring, evaluasi dan pelaporan keiatan subbidang</p><p>k. &nbsp; &nbsp; melaksanakan tugas-tugas kedinasan lainnya yang diberikan oleh Kepala Bidang sesuai dengan tugas dan fungsinya</p>', '<p>&nbsp;</p>'),
(101, 51, 'Sub Bidang Wilayah I', '<p>a.&nbsp;&nbsp;&nbsp; Menyusun dan membuat rencana kerja berdasarkan peraturan perundang-undangan</p><p>b.&nbsp;&nbsp;&nbsp; Melakukan evaluasi terhadap sumber penerimaan dan pengeluaran dari Rancangan Qanun tentang APBD dan Perubahan APBD kabupaten/kota</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan pembinaan pengelolaan keuangan daerah serta menginventarisasi permasalahan yang timbul berkaitan dengan teknis penyusunan APBK dan perubahan APBD kabupaten/kota</p><p>d.&nbsp;&nbsp;&nbsp; Melakukan monitoring dalam rangka meningkatkan kinerja perencanaan, penyusunan dan pelaksanaan dan pertanggungjawaban APBD kabupaten/kota</p><p>e.&nbsp;&nbsp;&nbsp; Melakukan konsultasi dengan pemerintah dalam rangka pembinaan dan vealuasi penyusunan dan pelaksanaan APBD kabupaten/kota</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp; Memfasilitasi permasalahan dan atau sengketa APBD dan perubahan APBD antara kepala daerah dan DPRD kabupaten/kota</p><p>g.&nbsp;&nbsp;&nbsp; Menyiapkan mekanisme penyaluran, pembinaandan pengawasan belanja bantuan keuangan kepada kabupaten/kota</p><p>h.&nbsp;&nbsp;&nbsp; Mengelola kegiatan pembinaan melalui bimtek, workshop dan sosialisasi untuk peningkatan sumber daya aparatur pengelola anggaran pendapatan dan belanja daerah kabupaten/kota dengan pengelola dana bantuan keuangan kabupaten/kota</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksankan evaluasi dan penilain terhadap kebenaran rancangan qonun tentang pertanggungjawaban pelaksanaan APBK kabupaten/kota dan rancangan qanun tentang penjabaran pertanggungjawaban pelaksanaan APBK kabupaten/kota</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melakukan koordinasi dan menghimpun data tentang pelaksanaan dan laporan pertanggungjawaban kabupaten/kota</p><p>k.&nbsp;&nbsp;&nbsp; Menyiapkan bahan-bahan untuk penyusunan rancangan peraturan gubernur tentang pembagian dana bagi hasil kepada kabupaten/kota</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melakukan penyaluran dana bagi hasil ke masing-masing kabupaten/kota</p><p>m.&nbsp; Melakukan monit', '<p>&nbsp;</p>'),
(102, 51, 'Sub Bidang Wilayah II', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp; Menyusun dan membuat rencana kerja berdasarkan peraturan perundang-undangan</p><p>b.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan evaluasi terhadap sumber penerimaan dan pengeluaran dari Rancangan Qanun tentang APBD dan Perubahan APBD kabupaten/kota</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melakukan pembinaan pengelolaan keuangan daerah serta menginventarisasi permasalahan yang timbul berkaitan dengan teknis penyusunan APBK dan perubahan APBD kabupaten/kota</p><p>d.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan monitoring dalam rangka meningkatkan kinerja perencanaan, penyusunan dan pelaksanaan dan pertanggungjawaban APBD kabupaten/kota</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp; Melakukan konsultasi dengan pemerintah dalam rangka pembinaan dan vealuasi penyusunan dan pelaksanaan APBD kabupaten/kota</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Memfasilitasi permasalahan dan atau sengketa APBD dan perubahan APBD antara kepala daerah dan DPRD kabupaten/kota</p><p>g.&nbsp;&nbsp;&nbsp;&nbsp; Menyiapkan mekanisme penyaluran, pembinaandan pengawasan belanja bantuan keuangan kepada kabupaten/kota</p><p>h.&nbsp;&nbsp;&nbsp;&nbsp; Mengelola kegiatan pembinaan melalui bimtek, workshop dan sosialisasi untuk peningkatan sumber daya aparatur pengelola anggaran pendapatan dan belanja daerah kabupaten/kota dengan pengelola dana bantuan keuangan kabupaten/kota</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melaksankan evaluasi dan penilain terhadap kebenaran rancangan qonun tentang pertanggungjawaban pelaksanaan APBK kabupaten/kota dan rancangan qanun tentang penjabaran pertanggungjawaban pelaksanaan APBK kabupaten/kota</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Melakukan koordinasi dan menghimpun data tentang pelaksanaan dan laporan pertanggungjawaban kabupaten/kota</p><p>k.&nbsp;&nbsp;&nbsp;&nbsp; Menyiapkan bahan-bahan untuk penyusunan rancangan peraturan gubernur tentang pembagian dana bagi hasil kepada kabupaten/kota</p><p>l.&nbsp;&nbsp;&nbsp;&nbsp;&nbs