(433, 214, 'Seksi Pelayanan Perizinan dan Nonperizinan B/I', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>b.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; merencanakan penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengolah penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>d.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memverifikasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengidentifikasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengkoordinasikan penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>g.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memvalidasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>h.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; membuat konsep penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; menyusun laporan penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengadministrasikan penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan; dan</p><p>menerbitkan penyelenggaraan pelayanan perizinan dan nonperizinan bidang energi dan sumber daya mineral serta pertanahan.</p>', '<p>&nbsp;</p>'),
(434, 214, 'Seksi Pelayanan Perizinan dan Nonperizinan B/II', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>b.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; merencanakan penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengolah penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>d.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memverifikasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengidentifikasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengkoordinasikan penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>g.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; menvalidasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>h.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; membuat konsep penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; menyusun laporan penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengadministrasi penyelenggaraan pelayanan&nbsp; perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan; dan</p><p>menerbitkan penyelenggaraan pelayanan perizinan dan nonperizinan bidang pangan, pertanian dan perkebunan.</p>', '<p>&nbsp;</p>'),
(435, 214, 'Seksi Pelayanan Perizinan dan Nonperizinan B/III', '<p>a.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melaksanakan penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>b.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; merencanakan penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengolah penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>d.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; memverifikasikan penyelenggaraan pelayanan perizinan&nbsp; dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>e.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengidentifikasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>f.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengkoordinasikan penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>g.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; menvalidasi penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>h.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; membuat konsep penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>i.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; menyusun laporan penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja;</p><p>j.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mengadministrasi penyelenggaraan pelayanan perizinan&nbsp; dan nonperizinan bidang transmigrasi dan tenaga kerja;&nbsp; dan</p><p>menerbitkan penyelenggaraan pelayanan perizinan dan nonperizinan bidang transmigrasi dan tenaga kerja.</p>', '<p>&nbsp;</p>'),
